import { db } from './server/db';
import { users, mealVouchers, hallBookings } from './shared/schema';

async function brutalAttackTest() {
  console.log('üî• BRUTAL ATTACK TESTING STARTED\n');

  // Attack 1: Rapid-fire concurrent requests (simulating DDoS)
  console.log('‚öîÔ∏è Attack 1: Simulating 100 concurrent API requests...');
  const start = Date.now();
  const requests = Array.from({ length: 100 }, () => 
    db.query.users.findMany({ limit: 1 })
  );

  try {
    await Promise.all(requests);
    const elapsed = Date.now() - start;
    console.log(`‚úÖ Handled 100 concurrent requests in ${elapsed}ms`);
  } catch (error) {
    console.log('‚ùå VULNERABILITY: Crashed under load!', error);
  }

  // Attack 2: Extreme data range queries
  console.log('\n‚öîÔ∏è Attack 2: Extreme date range queries...');
  try {
    const extremeDates = await db.query.hallBookings.findMany({
      where: (hallBookings, { between }) => 
        between(
          hallBookings.bookingStartTime,
          new Date('1900-01-01'),
          new Date('2100-12-31')
        ),
      limit: 1000
    });
    console.log(`‚úÖ Handled extreme date range query: ${extremeDates.length} results`);
  } catch (error) {
    console.log('‚ùå VULNERABILITY: Date range query failed!', error);
  }

