    await db.delete(hallBookings).where(eq(hallBookings.id, result[0].id));
  } catch (error: any) {
    if (error.message?.includes('out of range')) {
      console.log('‚úÖ PROTECTED: Numeric overflow prevented');
    } else {
      console.log('‚ö†Ô∏è  Extreme values error:', error.message);
    }
  }

  // Attack 5: Character encoding attacks
  console.log('\n‚öîÔ∏è Attack 5: Testing special character encoding...');
  try {
    const specialChars = await db.query.users.findMany({
      where: (users, { eq }) => eq(users.username, 'üëæüî•üíÄ<>"\'/\\'),
