import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { StatsCard } from "@/components/dashboard/stats-card";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Coffee, Clock, CheckCircle, XCircle, Package, AlertTriangle } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { useState } from "react";

interface KotItem {
  id: string;
  menuItemId: string;
  qty: number;
  notes: string | null;
  status: string;
  declineReason: string | null;
  menuItem?: { name: string };
}

interface KotOrder {
  id: string;
  tableId: string;
  status: string;
  createdAt: string;
  items?: KotItem[];
}

interface RestaurantTable {
  id: string;
  name: string;
}

interface InventoryItem {
  id: string;
  name: string;
  unit: string;
}

export default function BaristaDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();
  const [declineDialogOpen, setDeclineDialogOpen] = useState(false);
  const [wastageDialogOpen, setWastageDialogOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState<KotItem | null>(null);
  const [declineReason, setDeclineReason] = useState("");
  const [wastageData, setWastageData] = useState({
    itemId: "",
    qty: "",
    unit: "",
    reason: ""
  });

  const { data: kotOrders = [] } = useQuery({
    queryKey: ["/api/hotels/current/kot-orders"],
    refetchInterval: 3000,
    refetchIntervalInBackground: true
  });

  const { data: tables = [] } = useQuery({
    queryKey: ["/api/hotels/current/restaurant-tables"],
    refetchInterval: 5000,
    refetchIntervalInBackground: true
  });

  const { data: inventoryItems = [] } = useQuery({
    queryKey: ["/api/hotels/current/inventory-items"]
  });

  const updateKotItemMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return await apiRequest("PUT", `/api/kot-items/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/kot-orders"] });
      toast({ title: "KOT item updated successfully" });
      setDeclineDialogOpen(false);
      setDeclineReason("");
      setSelectedItem(null);
    },
    onError: (error: Error) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    }
  });

  const createWastageMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/hotels/current/wastages", data);
    },
    onSuccess: () => {
      toast({ title: "Wastage recorded successfully" });
      setWastageDialogOpen(false);
      setWastageData({ itemId: "", qty: "", unit: "", reason: "" });
    },
    onError: (error: Error) => {
      toast({ title: "Error", description: error.message, variant: "destructive" });
    }
  });

  const handleApprove = (item: KotItem) => {
    updateKotItemMutation.mutate({
      id: item.id,
      data: { status: "approved" }
    });
  };

  const handleDecline = (item: KotItem) => {
    setSelectedItem(item);
    setDeclineDialogOpen(true);
  };

  const handleSetReady = (item: KotItem) => {
    updateKotItemMutation.mutate({
      id: item.id,
      data: { status: "ready" }
    });
  };

  const submitDecline = () => {
    if (!selectedItem || !declineReason.trim()) {
      toast({ title: "Please provide a reason for declining", variant: "destructive" });
      return;
    }
    if (declineReason.trim().length < 10) {
      toast({ title: "Decline reason must be at least 10 characters", variant: "destructive" });
      return;
    }
    updateKotItemMutation.mutate({
      id: selectedItem.id,
      data: { status: "declined", declineReason: declineReason.trim() }
    });
  };

  const submitWastage = () => {
    if (!wastageData.itemId || !wastageData.qty || !wastageData.reason.trim()) {
      toast({ title: "Please fill all wastage fields", variant: "destructive" });
      return;
    }
    const qty = parseFloat(wastageData.qty);
    if (isNaN(qty) || qty <= 0) {
      toast({ title: "Please enter a valid quantity", variant: "destructive" });
      return;
    }
    createWastageMutation.mutate({
      itemId: wastageData.itemId,
      qty: qty.toString(),
      unit: wastageData.unit,
      reason: wastageData.reason.trim()
    });
  };

  const pendingOrders = Array.isArray(kotOrders) ? (kotOrders as KotOrder[]).filter((order: KotOrder) => 
    order.items?.some((item: KotItem) => item.status === 'pending')
  ) : [];

  const approvedOrders = Array.isArray(kotOrders) ? (kotOrders as KotOrder[]).filter((order: KotOrder) => 
    order.items?.some((item: KotItem) => item.status === 'approved')
  ) : [];

  const readyOrders = Array.isArray(kotOrders) ? (kotOrders as KotOrder[]).filter((order: KotOrder) => 
    order.items?.every((item: KotItem) => item.status === 'ready')
  ) : [];

  const declinedOrders = Array.isArray(kotOrders) ? (kotOrders as KotOrder[]).filter((order: KotOrder) => 
    order.items?.some((item: KotItem) => item.status === 'declined')
  ) : [];

  const getTableNumber = (tableId: string) => {
    if (!Array.isArray(tables)) return tableId;
    const table = (tables as RestaurantTable[]).find((t: RestaurantTable) => t.id === tableId);
    return table?.name || tableId;
  };

  const renderKotCard = (order: KotOrder) => (
    <Card key={order.id}>
      <CardHeader>
        <CardTitle className="flex justify-between items-center">
          <span>{getTableNumber(order.tableId)}</span>
          <span className="text-sm text-gray-500">
            {new Date(order.createdAt).toLocaleTimeString()}
          </span>
        </CardTitle>
      </CardHeader>
      <CardContent>
        {order.items?.map((item: KotItem) => (
          <div key={item.id} className="mb-4 p-3 border rounded">
            <div className="flex justify-between items-start mb-2">
              <div>
                <p className="font-semibold">{item.menuItem?.name}</p>
                <p className="text-sm text-gray-600">Quantity: {item.qty}</p>
                {item.notes && <p className="text-sm text-gray-500">Notes: {item.notes}</p>}
                {item.declineReason && (
                  <p className="text-sm text-red-600">Declined: {item.declineReason}</p>
                )}
              </div>
              <Badge variant={
                item.status === 'approved' ? 'default' :
                item.status === 'ready' ? 'default' :
                item.status === 'declined' ? 'destructive' : 'secondary'
              }>
                {item.status}
              </Badge>
            </div>
            {item.status === 'pending' && (
              <div className="flex gap-2 mt-2">
                <Button
                  size="sm"
                  onClick={() => handleApprove(item)}
                  className="flex-1"
                >
                  <CheckCircle className="w-4 h-4 mr-1" /> Approve
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  onClick={() => handleDecline(item)}
                  className="flex-1"
                >
                  <XCircle className="w-4 h-4 mr-1" /> Decline
                </Button>
              </div>
            )}
            {item.status === 'approved' && (
              <Button
                size="sm"
                onClick={() => handleSetReady(item)}
                className="w-full mt-2"
              >
                <Clock className="w-4 h-4 mr-1" /> Set Ready
              </Button>
            )}
          </div>
        ))}
      </CardContent>
    </Card>
  );

  return (
    <DashboardLayout title="Barista Dashboard">
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatsCard
            title="Pending Orders"
            value={pendingOrders.length}
            icon={<Clock />}
            iconColor="text-orange-500"
          />
          <StatsCard
            title="Brewing"
            value={approvedOrders.length}
            icon={<Coffee />}
            iconColor="text-brown-500"
          />
          <StatsCard
            title="Ready to Serve"
            value={readyOrders.length}
            icon={<CheckCircle />}
            iconColor="text-green-500"
          />
          <StatsCard
            title="Declined"
            value={declinedOrders.length}
            icon={<XCircle />}
            iconColor="text-red-500"
          />
        </div>

        <div className="flex gap-2 justify-end">
          <Dialog open={wastageDialogOpen} onOpenChange={setWastageDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline">
                <Package className="w-4 h-4 mr-2" /> Record Wastage
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Record Wastage</DialogTitle>
                <DialogDescription>Record inventory wastage with reason</DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label>Inventory Item</Label>
                  <Select
                    value={wastageData.itemId}
                    onValueChange={(value) => {
                      const selectedInventoryItem = (inventoryItems as InventoryItem[]).find(item => item.id === value);
                      setWastageData({ 
                        ...wastageData, 
                        itemId: value,
                        unit: selectedInventoryItem?.unit || 'piece'
                      });
                    }}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select item" />
                    </SelectTrigger>
                    <SelectContent>
                      {Array.isArray(inventoryItems) && (inventoryItems as InventoryItem[]).map((item: InventoryItem) => (
                        <SelectItem key={item.id} value={item.id}>
                          {item.name} ({item.unit})
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label>Quantity</Label>
                  <Input
                    type="number"
                    step="0.01"
                    value={wastageData.qty}
                    onChange={(e) => setWastageData({ ...wastageData, qty: e.target.value })}
                    placeholder="Enter quantity"
                  />
                </div>
                <div>
                  <Label>Reason</Label>
                  <Textarea
                    value={wastageData.reason}
                    onChange={(e) => setWastageData({ ...wastageData, reason: e.target.value })}
                    placeholder="Explain reason for wastage"
                  />
                </div>
                <Button onClick={submitWastage} className="w-full" disabled={createWastageMutation.isPending}>
                  Record Wastage
                </Button>
              </div>
            </DialogContent>
          </Dialog>

        </div>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Coffee className="h-5 w-5 text-brown-500" />
              <span>Coffee Orders</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <h3 className="font-semibold mb-2">Pending Orders ({pendingOrders.length})</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {pendingOrders.length === 0 ? (
                    <p className="text-gray-500 col-span-full text-center py-4">No pending orders</p>
                  ) : (
                    pendingOrders.map(renderKotCard)
                  )}
                </div>
              </div>

              <div>
                <h3 className="font-semibold mb-2">Brewing ({approvedOrders.length})</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {approvedOrders.length === 0 ? (
                    <p className="text-gray-500 col-span-full text-center py-4">No orders brewing</p>
                  ) : (
                    approvedOrders.map(renderKotCard)
                  )}
                </div>
              </div>

              <div>
                <h3 className="font-semibold mb-2">Ready ({readyOrders.length})</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {readyOrders.length === 0 ? (
                    <p className="text-gray-500 col-span-full text-center py-4">No ready orders</p>
                  ) : (
                    readyOrders.map(renderKotCard)
                  )}
                </div>
              </div>

              <div>
                <h3 className="font-semibold mb-2">Declined ({declinedOrders.length})</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {declinedOrders.length === 0 ? (
                    <p className="text-gray-500 col-span-full text-center py-4">No declined orders</p>
                  ) : (
                    declinedOrders.map(renderKotCard)
                  )}
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <Dialog open={declineDialogOpen} onOpenChange={setDeclineDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Decline KOT Item</DialogTitle>
            <DialogDescription>Please provide a reason for declining this item</DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label>Reason for Decline</Label>
              <Textarea
                value={declineReason}
                onChange={(e) => setDeclineReason(e.target.value)}
                placeholder="e.g., Out of stock, Machine malfunction"
              />
            </div>
            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setDeclineDialogOpen(false)} className="flex-1">
                Cancel
              </Button>
              <Button onClick={submitDecline} className="flex-1" variant="destructive" disabled={updateKotItemMutation.isPending}>
                Decline
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/owner/reports.content:
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { StatsCard } from "@/components/dashboard/stats-card";
import { BarChart, FileText, Download, TrendingUp, DollarSign } from "lucide-react";
import { formatCurrency } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";
import { useRealtimeQuery } from "@/hooks/use-realtime-query";

export default function Reports() {
  const { toast } = useToast();
  const [dateRange, setDateRange] = useState({
    from: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    to: new Date().toISOString().split('T')[0]
  });

  const [reportType, setReportType] = useState('financial');

  const { data: transactions = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/transactions"],
    refetchInterval: 3000
  });

  const { data: rooms = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/rooms"],
    refetchInterval: 3000
  });

  const { data: staff = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/users"],
    refetchInterval: 3000
  });

  const { data: inventory = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/inventory-items"],
    refetchInterval: 3000
  });

  const { data: dailyAttendance = [] } = useQuery<any[]>({
    queryKey: ["/api/attendance/daily"],
    refetchInterval: 3000
  });

  const { data: vendors = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/vendors"],
    refetchInterval: 3000
  });

  // Real-time updates
  useRealtimeQuery({
    queryKey: ["/api/hotels/current/transactions"],
    refetchInterval: 3000,
    events: ['transaction:created', 'transaction:updated']
  });

  useRealtimeQuery({
    queryKey: ["/api/hotels/current/rooms"],
    refetchInterval: 3000,
    events: ['room:updated', 'room:created']
  });

  useRealtimeQuery({
    queryKey: ["/api/hotels/current/users"],
    refetchInterval: 3000,
    events: ['user:created', 'user:updated']
  });

  useRealtimeQuery({
    queryKey: ["/api/hotels/current/inventory-items"],
    refetchInterval: 3000,
    events: ['inventory:created', 'inventory:updated', 'inventory:deleted']
  });

  useRealtimeQuery({
    queryKey: ["/api/attendance/daily"],
    refetchInterval: 3000,
    events: ['attendance:updated']
  });

  const filteredTransactions = transactions.filter(t => {
    const txnDate = new Date(t.createdAt);
    const fromDate = new Date(dateRange.from);
    const toDate = new Date(dateRange.to);
    toDate.setHours(23, 59, 59, 999);
    return txnDate >= fromDate && txnDate <= toDate;
  });

  const totalRevenue = filteredTransactions
    .filter(t => t.txnType === 'revenue')
    .reduce((sum, t) => sum + (Number(t.amount) || 0), 0);

  const totalExpenses = filteredTransactions
    .filter(t => t.txnType === 'expense')
    .reduce((sum, t) => sum + (Number(t.amount) || 0), 0);

  const occupancyRate = rooms.length > 0 ? 
    (rooms.filter(r => r.isOccupied).length / rooms.length) * 100 : 0;

  const activeStaff = staff.filter(s => s.isActive).length;

  const downloadCSV = (csvContent: string, filename: string) => {
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const exportReport = (format: string) => {
    const dateFrom = new Date(dateRange.from).toLocaleDateString();
    const dateTo = new Date(dateRange.to).toLocaleDateString();

    if (format === 'financial') {
      let csv = `Financial Report (${dateFrom} - ${dateTo})\n\n`;
      csv += `SUMMARY\n`;
      csv += `Total Revenue,${totalRevenue}\n`;
      csv += `Total Expenses,${totalExpenses}\n`;
      csv += `Net Profit,${totalRevenue - totalExpenses}\n`;
      csv += `Profit Margin,${totalRevenue > 0 ? ((totalRevenue - totalExpenses) / totalRevenue * 100).toFixed(1) : 0}%\n\n`;

      csv += `TRANSACTIONS\n`;
      csv += `Date,Type,Purpose,Amount,Payment Method\n`;
      filteredTransactions.forEach(txn => {
        csv += `${new Date(txn.createdAt).toLocaleDateString()},${txn.txnType},"${txn.purpose || ''}",${txn.amount},${txn.paymentMethod || ''}\n`;
      });

      downloadCSV(csv, `financial_report_${dateRange.from}_to_${dateRange.to}.csv`);
      toast({ title: "Financial Report Exported", description: "CSV file downloaded successfully" });
    } else if (format === 'occupancy') {
      let csv = `Occupancy Report (${dateFrom} - ${dateTo})\n\n`;
      csv += `Room Number,Type,Status,Occupied\n`;
      rooms.forEach(room => {
        csv += `${room.roomNumber},${room.type || 'Standard'},${room.isOccupied ? 'Occupied' : 'Available'},${room.isOccupied ? 'Yes' : 'No'}\n`;
      });
      csv += `\nSUMMARY\n`;
      csv += `Total Rooms,${rooms.length}\n`;
      csv += `Occupied Rooms,${rooms.filter(r => r.isOccupied).length}\n`;
      csv += `Available Rooms,${rooms.filter(r => !r.isOccupied).length}\n`;
      csv += `Occupancy Rate,${occupancyRate.toFixed(1)}%\n`;

      downloadCSV(csv, `occupancy_report_${dateRange.from}_to_${dateRange.to}.csv`);
      toast({ title: "Occupancy Report Exported", description: "CSV file downloaded successfully" });
    } else if (format === 'staff') {
      let csv = `Staff Report (${dateFrom} - ${dateTo})\n\n`;
      csv += `Username,Full Name,Role,Status,Email\n`;
      staff.forEach(member => {
        csv += `${member.username},${member.fullName || ''},${member.role?.name || ''},${member.isActive ? 'Active' : 'Inactive'},${member.email || ''}\n`;
      });
      csv += `\nSUMMARY\n`;
      csv += `Total Staff,${staff.length}\n`;
      csv += `Active Staff,${activeStaff}\n`;
      csv += `Inactive Staff,${staff.length - activeStaff}\n`;

      downloadCSV(csv, `staff_report_${dateRange.from}_to_${dateRange.to}.csv`);
      toast({ title: "Staff Report Exported", description: "CSV file downloaded successfully" });
    }
  };

  const generateReport = () => {
    exportReport(reportType);
  };

  return (
    <DashboardLayout title="Financial Report">
      <div className="space-y-6">
        <div>
          <h2 className="text-2xl font-bold">Financial Reports</h2>
          <p className="text-muted-foreground">Comprehensive financial insights and analytics</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatsCard
            title="Total Revenue"
            value={formatCurrency(totalRevenue)}
            icon={<DollarSign />}
            iconColor="text-green-500"
          />
          <StatsCard
            title="Total Expenses"
            value={formatCurrency(totalExpenses)}
            icon={<TrendingUp />}
            iconColor="text-red-500"
          />
          <StatsCard
            title="Occupancy Rate"
            value={`${occupancyRate.toFixed(1)}%`}
            icon={<BarChart />}
            iconColor="text-blue-500"
          />
          <StatsCard
            title="Active Staff"
            value={activeStaff}
            icon={<FileText />}
            iconColor="text-purple-500"
          />
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <FileText className="h-5 w-5" />
              Generate Report
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="space-y-2">
                <Label htmlFor="report-type">Report Type</Label>
                <Select value={reportType} onValueChange={setReportType}>
                  <SelectTrigger data-testid="select-report-type">
                    <SelectValue placeholder="Select report type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="financial">Financial Report</SelectItem>
                    <SelectItem value="occupancy">Occupancy Report</SelectItem>
                    <SelectItem value="staff">Staff Report</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="date-from">From Date</Label>
                <Input
                  id="date-from"
                  type="date"
                  value={dateRange.from}
                  onChange={(e) => setDateRange(prev => ({ ...prev, from: e.target.value }))}
                  data-testid="input-date-from"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="date-to">To Date</Label>
                <Input
                  id="date-to"
                  type="date"
                  value={dateRange.to}
                  onChange={(e) => setDateRange(prev => ({ ...prev, to: e.target.value }))}
                  data-testid="input-date-to"
                />
              </div>

              <div className="space-y-2">
                <Label>Actions</Label>
                <div className="flex gap-2">
                  <Button onClick={generateReport} className="flex-1" data-testid="button-generate">
                    <Download className="h-4 w-4 mr-2" />
                    Generate
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Financial Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span>Total Revenue:</span>
                  <span className="font-bold text-green-600">{formatCurrency(totalRevenue)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Total Expenses:</span>
                  <span className="font-bold text-red-600">{formatCurrency(totalExpenses)}</span>
                </div>
                <div className="flex justify-between border-t pt-2">
                  <span>Net Profit:</span>
                  <span className="font-bold">{formatCurrency(totalRevenue - totalExpenses)}</span>
                </div>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="w-full"
                  onClick={() => exportReport('financial')}
                  data-testid="button-export-financial"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Export Financial Report
                </Button>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Occupancy Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span>Total Rooms:</span>
                  <span className="font-bold">{rooms.length}</span>
                </div>
                <div className="flex justify-between">
                  <span>Occupied:</span>
                  <span className="font-bold">{rooms.filter(r => r.isOccupied).length}</span>
                </div>
                <div className="flex justify-between border-t pt-2">
                  <span>Occupancy Rate:</span>
                  <span className="font-bold">{occupancyRate.toFixed(1)}%</span>
                </div>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="w-full"
                  onClick={() => exportReport('occupancy')}
                  data-testid="button-export-occupancy"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Export Occupancy Report
                </Button>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Staff Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span>Total Staff:</span>
                  <span className="font-bold">{staff.length}</span>
                </div>
                <div className="flex justify-between">
                  <span>Active:</span>
                  <span className="font-bold text-green-600">{activeStaff}</span>
                </div>
                <div className="flex justify-between border-t pt-2">
                  <span>On Duty Now:</span>
                  <span className="font-bold">{staff.filter(s => dailyAttendance.some(a => a.userId === s.id && a.status === 'active')).length}</span>
                </div>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="w-full"
                  onClick={() => exportReport('staff')}
                  data-testid="button-export-staff"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Export Staff Report
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Recent Activity Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <h4 className="font-medium mb-2">Recent Transactions</h4>
                  <div className="space-y-2">
                    {filteredTransactions.length > 0 ? (
                      filteredTransactions.slice(0, 5).map((transaction, index) => (
                        <div key={transaction.id || index} className="flex justify-between text-sm">
                          <span className="truncate">{transaction.purpose || transaction.txnType?.replace(/_/g, ' ')}</span>
                          <span className={`font-medium ${
                            transaction.txnType === 'revenue' ? 'text-green-600' : 'text-red-600'
                          }`}>
                            {formatCurrency(Number(transaction.amount))}
                          </span>
                        </div>
                      ))
                    ) : (
                      <p className="text-sm text-muted-foreground">No transactions in selected date range</p>
                    )}
                  </div>
                </div>

                <div>
                  <h4 className="font-medium mb-2">Low Stock Items</h4>
                  <div className="space-y-2">
                    {inventory
                      .filter(item => Number(item.stockQty) <= Number(item.reorderLevel))
                      .slice(0, 5)
                      .map((item, index) => (
                        <div key={item.id || index} className="flex justify-between text-sm">
                          <span className="truncate">{item.name}</span>
                          <span className="text-orange-600 font-medium">
                            {item.stockQty} {item.unit}
                          </span>
                        </div>
                      ))}
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

