                <Label htmlFor="add-room-number">Room Number</Label>
                <Input
                  id="add-room-number"
                  value={roomNumber}
                  onChange={(e) => setRoomNumber(e.target.value)}
                  placeholder="Enter room number"
                  data-testid="input-room-number"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="add-room-type">Room Type</Label>
                <Select value={selectedRoomTypeId} onValueChange={setSelectedRoomTypeId}>
                  <SelectTrigger id="add-room-type" data-testid="select-room-type">
                    <SelectValue placeholder="Select room type" />
                  </SelectTrigger>
                  <SelectContent>
                    {roomTypes.map((type: any) => (
                      <SelectItem key={type.id} value={type.id.toString()}>
                        {type.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => setAddDialog(false)} data-testid="button-cancel-add">Cancel</Button>
                <Button onClick={handleSaveRoom} disabled={createRoomMutation.isPending} data-testid="button-create-room">
                  {createRoomMutation.isPending ? "Creating..." : "Create Room"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>

        {/* Add/Edit Room Type Dialog */}
        <Dialog open={roomTypeDialog || editRoomTypeDialog} onOpenChange={(open) => {
          if (!open) {
            setRoomTypeDialog(false);
            setEditRoomTypeDialog(false);
            resetRoomTypeForm();
          }
        }}>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{editRoomTypeDialog ? 'Edit Room Type' : 'Add Room Type'}</DialogTitle>
              <DialogDescription>
                {editRoomTypeDialog ? 'Update room type and pricing information' : 'Create a new room type with pricing'}
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="room-type-name">Room Type Name *</Label>
                <Input
                  id="room-type-name"
                  value={roomTypeName}
                  onChange={(e) => setRoomTypeName(e.target.value)}
                  placeholder="e.g., Deluxe, Standard, Suite"
                  data-testid="input-room-type-name"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="price-walkin">Walk-in Price (NPR) *</Label>
                <Input
                  id="price-walkin"
                  type="number"
                  value={priceWalkin}
                  onChange={(e) => setPriceWalkin(e.target.value)}
                  placeholder="Enter walk-in price"
                  data-testid="input-price-walkin"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="price-inhouse">In-house Price (NPR) *</Label>
                <Input
                  id="price-inhouse"
                  type="number"
                  value={priceInhouse}
                  onChange={(e) => setPriceInhouse(e.target.value)}
                  placeholder="Enter in-house price"
                  data-testid="input-price-inhouse"
                />
              </div>
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => {
                  setRoomTypeDialog(false);
                  setEditRoomTypeDialog(false);
                  resetRoomTypeForm();
                }} data-testid="button-cancel-room-type">
                  Cancel
                </Button>
                <Button 
                  onClick={handleSaveRoomType} 
                  disabled={createRoomTypeMutation.isPending || updateRoomTypeMutation.isPending}
                  data-testid="button-save-room-type"
                >
                  {(createRoomTypeMutation.isPending || updateRoomTypeMutation.isPending) ? "Saving..." : editRoomTypeDialog ? "Update" : "Create"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/housekeeping-.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { getStatusColor } from "@/lib/utils";
import { CheckSquare, Clock } from "lucide-react";
import { StatsCard } from "@/components/dashboard/stats-card";

export default function HousekeepingStaffMyTasks() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: tasks = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/users", user?.id, "tasks"],
    enabled: !!user?.id,
    refetchInterval: 5000,
    refetchIntervalInBackground: true
  });

  const updateTaskMutation = useMutation({
    mutationFn: async ({ taskId, status }: { taskId: string; status: string }) => {
      await apiRequest("PUT", `/api/hotels/current/tasks/${taskId}`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users", user?.id, "tasks"] });
      toast({ title: "Task updated successfully" });
    },
    onError: (error: any) => {
      toast({ title: error.message || "Failed to update task", variant: "destructive" });
    }
  });

  const pendingTasks = tasks.filter((t: any) => t.status === 'pending');
  const performingTasks = tasks.filter((t: any) => t.status === 'in_progress');
  const completedTasks = tasks.filter((t: any) => t.status === 'completed' || t.status === 'pending_review');

  const handleTaskStatusUpdate = (task: any, newStatus: string) => {
    updateTaskMutation.mutate({ taskId: task.id, status: newStatus });
  };

  return (
    <DashboardLayout title="My Tasks">
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <StatsCard
            title="Pending Tasks"
            value={pendingTasks.length}
            icon={<Clock />}
            iconColor="text-orange-500"
          />
          <StatsCard
            title="In Progress"
            value={performingTasks.length}
            icon={<CheckSquare />}
            iconColor="text-blue-500"
          />
          <StatsCard
            title="Completed"
            value={completedTasks.length}
            icon={<CheckSquare />}
            iconColor="text-green-500"
          />
        </div>

        <Card>
          <CardHeader>
            <CardTitle>All My Tasks</CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <p className="text-muted-foreground">Loading tasks...</p>
            ) : tasks.length === 0 ? (
              <p className="text-center text-muted-foreground py-4" data-testid="no-tasks-message">
                No tasks assigned
              </p>
            ) : (
              <div className="space-y-3">
                {tasks.map((task, index) => (
                  <div key={task.id} className="flex items-center justify-between p-4 border rounded-lg" data-testid={`task-item-${index}`}>
                    <div className="flex-1">
                      <h4 className="font-medium text-foreground">{task.title}</h4>
                      {task.description && (
                        <p className="text-sm text-muted-foreground mt-1">{task.description}</p>
                      )}
                      <div className="flex items-center space-x-3 mt-2">
                        <Badge className={getStatusColor(task.status)} variant="secondary">
                          {task.status}
                        </Badge>
                        {task.priority && (
                          <Badge variant="outline" className="text-xs">
                            Priority: {task.priority}
                          </Badge>
                        )}
                        <span className="text-xs text-muted-foreground">
                          Created: {new Date(task.createdAt).toLocaleDateString('en-GB', { timeZone: 'Asia/Kathmandu' })}
                        </span>
                      </div>
                    </div>
                    <div className="flex flex-col space-y-2 ml-4">
                      {task.status === 'pending' && (
                        <Button
                          size="sm"
                          onClick={() => handleTaskStatusUpdate(task, 'in_progress')}
                          disabled={updateTaskMutation.isPending}
                          data-testid={`button-start-task-${index}`}
                        >
                          Start Task
                        </Button>
                      )}
                      {task.status === 'in_progress' && (
                        <Button
                          size="sm"
                          onClick={() => handleTaskStatusUpdate(task, 'completed')}
                          disabled={updateTaskMutation.isPending}
                          data-testid={`button-complete-task-${index}`}
                        >
                          Mark Complete
                        </Button>
                      )}
                      {task.status === 'pending_review' && (
                        <Badge className="bg-yellow-100 text-yellow-800" variant="secondary">
                          Pending Approval
                        </Badge>
                      )}
                      {task.status === 'completed' && (
                        <Badge className="bg-green-100 text-green-800" variant="secondary">
                          Done ‚úì
                        </Badge>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

