import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Calculator, Save, Settings } from "lucide-react";
import { useRealtimeQuery } from "@/hooks/use-realtime-query";

interface TaxSetting {
  id?: string;
  taxType: string;
  percent: number;
  isActive: boolean;
}

export default function TaxConfiguration() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: hotelTaxes = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/taxes"],
    refetchInterval: 3000
  });

  const { data: hotel } = useQuery<any>({
    queryKey: ["/api/hotels/current"],
    refetchInterval: 3000
  });

  // Real-time updates for tax configuration changes
  useRealtimeQuery({
    queryKey: ["/api/hotels/current/taxes"],
    events: ['tax:updated', 'tax:created']
  });

  // Initialize tax settings with default values
  const [taxSettings, setTaxSettings] = useState<Record<string, TaxSetting>>({
    vat: { taxType: 'vat', percent: 13, isActive: false },
    service_tax: { taxType: 'service_tax', percent: 10, isActive: false },
    luxury_tax: { taxType: 'luxury_tax', percent: 5, isActive: false }
  });

  // Update tax settings when hotel taxes data is loaded
  useEffect(() => {
    if (hotelTaxes && hotelTaxes.length > 0) {
      const updatedSettings = { ...taxSettings };
      hotelTaxes.forEach((tax: any) => {
        if (tax.taxType in updatedSettings) {
          updatedSettings[tax.taxType] = {
            id: tax.id,
            taxType: tax.taxType,
            percent: parseFloat(tax.percent) || 0,
            isActive: tax.isActive || false
          };
        }
      });
      setTaxSettings(updatedSettings);
    }
  }, [hotelTaxes]);

  const updateTaxMutation = useMutation({
    mutationFn: async (taxData: TaxSetting) => {
      const response = await fetch("/api/hotels/current/taxes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...taxData,
          percentType: 'number'
        }),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to update tax settings");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/taxes"] });
      toast({
        title: "Success",
        description: "Tax settings updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update tax settings",
        variant: "destructive",
      });
    },
  });

  const handleTaxToggle = (taxType: string, enabled: boolean) => {
    const updatedTax = { ...taxSettings[taxType], isActive: enabled };
    setTaxSettings(prev => ({
      ...prev,
      [taxType]: updatedTax
    }));
    // Automatically save when toggled
    updateTaxMutation.mutate(updatedTax);
  };

  const handlePercentChange = (taxType: string, percent: number) => {
    setTaxSettings(prev => ({
      ...prev,
      [taxType]: { ...prev[taxType], percent }
    }));
    // Note: Don't auto-save on percent change to allow user to finish typing
  };

  const handleSaveTax = (taxType: string) => {
    const taxData = taxSettings[taxType];
    updateTaxMutation.mutate(taxData);
  };

  const calculateTaxExample = (amount: number) => {
    let runningTotal = amount;
    let breakdown: any = { subtotal: amount };

    // Apply taxes in order: VAT -> Service Tax -> Luxury Tax (cascading)
    const taxOrder = ['vat', 'service_tax', 'luxury_tax'];

    taxOrder.forEach((key) => {
      const tax = taxSettings[key];
      if (tax && tax.isActive) {
        const taxAmount = Math.round((runningTotal * tax.percent) / 100 * 100) / 100;
        breakdown[key] = taxAmount;
        runningTotal = Math.round((runningTotal + taxAmount) * 100) / 100;
      }
    });

    return { total: runningTotal, breakdown };
  };

  const exampleAmount = 1000;
  const taxExample = calculateTaxExample(exampleAmount);

  return (
    <DashboardLayout title="Tax Configuration">
      <div className="space-y-6">
        {/* Tax Configuration Header */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calculator className="h-5 w-5" />
              Tax Settings
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* VAT Configuration */}
              <div className="space-y-4 p-4 border rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-foreground">VAT</h4>
                    <p className="text-sm text-muted-foreground">Value Added Tax</p>
                  </div>
                  <Switch
                    checked={taxSettings.vat.isActive}
                    onCheckedChange={(checked) => handleTaxToggle('vat', checked)}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="vat-percent">Tax Percentage</Label>
                  <div className="flex gap-2">
                    <Input
                      id="vat-percent"
                      type="number"
                      value={taxSettings.vat.percent}
                      onChange={(e) => handlePercentChange('vat', Number(e.target.value))}
                      disabled={!taxSettings.vat.isActive}
                      min="0"
                      max="100"
                      step="0.1"
                    />
                    <Button 
                      size="sm" 
                      onClick={() => handleSaveTax('vat')}
                      disabled={updateTaxMutation.isPending}
                    >
                      <Save className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>

              {/* Service Tax Configuration */}
              <div className="space-y-4 p-4 border rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-foreground">Service Tax</h4>
                    <p className="text-sm text-muted-foreground">Service charge</p>
                  </div>
                  <Switch
                    checked={taxSettings.service_tax.isActive}
                    onCheckedChange={(checked) => handleTaxToggle('service_tax', checked)}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="service-percent">Tax Percentage</Label>
                  <div className="flex gap-2">
                    <Input
                      id="service-percent"
                      type="number"
                      value={taxSettings.service_tax.percent}
                      onChange={(e) => handlePercentChange('service_tax', Number(e.target.value))}
                      disabled={!taxSettings.service_tax.isActive}
                      min="0"
                      max="100"
                      step="0.1"
                    />
                    <Button 
                      size="sm" 
                      onClick={() => handleSaveTax('service_tax')}
                      disabled={updateTaxMutation.isPending}
                    >
                      <Save className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>

              {/* Luxury Tax Configuration */}
              <div className="space-y-4 p-4 border rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-foreground">Luxury Tax</h4>
                    <p className="text-sm text-muted-foreground">Premium services</p>
                  </div>
                  <Switch
                    checked={taxSettings.luxury_tax.isActive}
                    onCheckedChange={(checked) => handleTaxToggle('luxury_tax', checked)}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="luxury-percent">Tax Percentage</Label>
                  <div className="flex gap-2">
                    <Input
                      id="luxury-percent"
                      type="number"
                      value={taxSettings.luxury_tax.percent}
                      onChange={(e) => handlePercentChange('luxury_tax', Number(e.target.value))}
                      disabled={!taxSettings.luxury_tax.isActive}
                      min="0"
                      max="100"
                      step="0.1"
                    />
                    <Button 
                      size="sm" 
                      onClick={() => handleSaveTax('luxury_tax')}
                      disabled={updateTaxMutation.isPending}
                    >
                      <Save className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Tax Calculation Example */}
        <Card>
          <CardHeader>
            <CardTitle>Tax Calculation Example</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="text-sm text-muted-foreground">
                Example calculation for NPR {exampleAmount.toLocaleString()}:
              </div>

              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span>NPR {taxExample.breakdown.subtotal.toLocaleString()}</span>
                </div>

                {Object.entries(taxSettings).map(([key, tax]) => {
                  if (!tax.isActive) return null;
                  const taxAmount = taxExample.breakdown[key] || 0;
                  return (
                    <div key={key} className="flex justify-between text-sm">
                      <span>{tax.taxType.replace('_', ' ').toUpperCase()} ({tax.percent}%):</span>
                      <span>NPR {taxAmount.toLocaleString()}</span>
                    </div>
                  );
                })}

                <div className="border-t pt-2 flex justify-between font-bold">
                  <span>Total:</span>
                  <span>NPR {taxExample.total.toLocaleString()}</span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Hotel Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Settings className="h-5 w-5" />
              Hotel Tax Information
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="vat-number">VAT Number</Label>
                  <Input 
                    id="vat-number" 
                    placeholder="Enter VAT registration number" 
                    defaultValue={hotel?.vatNo || ''}
                  />
                </div>
                <div>
                  <Label htmlFor="business-address">Business Address</Label>
                  <Input 
                    id="business-address" 
                    placeholder="Enter business address" 
                    defaultValue={hotel?.address || ''}
                  />
                </div>
              </div>
              <Button className="mt-4" onClick={() => {
                const vatNumber = (document.getElementById('vat-number') as HTMLInputElement)?.value;
                const address = (document.getElementById('business-address') as HTMLInputElement)?.value;
                // TODO: Add hotel update mutation
                toast({
                  title: "Feature Coming Soon",
                  description: "Hotel information update will be implemented",
                });
              }}>
                Update Hotel Information
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/manager/atten.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { DataTable } from "@/components/tables/data-table";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { StatsCard } from "@/components/dashboard/stats-card";
import { Badge } from "@/components/ui/badge";
import { Calendar, Clock, Users, UserCheck } from "lucide-react";
import { formatDateTime, cn } from "@/lib/utils";
import { format, startOfMonth, endOfMonth, parseISO } from "date-fns";

export default function AttendanceReports() {
  const [selectedMonth, setSelectedMonth] = useState(new Date());

  const { data: allStaff = [], isLoading: staffLoading } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/users"],
    refetchInterval: 3000
  });

  const { data: dailyAttendance = [], isLoading: attendanceLoading } = useQuery<any[]>({
    queryKey: ["/api/attendance/daily"],
    refetchInterval: 3000
  });

  const { data: allAttendance = [], isLoading: allAttendanceLoading } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/attendance"],
    queryFn: async () => {
      const response = await fetch("/api/hotels/current/attendance", { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch attendance");
      return response.json();
    },
    refetchInterval: 3000
  });

  const activeAttendanceUserIds = dailyAttendance
    .filter(a => a.status === 'active')
    .map(a => a.userId);

  const onDutyStaff = allStaff.filter(s => activeAttendanceUserIds.includes(s.id));
  const offDutyStaff = allStaff.filter(s => !activeAttendanceUserIds.includes(s.id));

  const monthStart = startOfMonth(selectedMonth);
  const monthEnd = endOfMonth(selectedMonth);

  const monthlyAttendance = allAttendance.filter(record => {
    if (!record.clockInTime) return false;
    const clockInDate = parseISO(record.clockInTime);
    return clockInDate >= monthStart && clockInDate <= monthEnd;
  });

  const totalHoursWorked = monthlyAttendance.reduce((sum, record) => {
    return sum + (Number(record.totalHours) || 0);
  }, 0);

  const avgHoursPerDay = monthlyAttendance.length > 0 
    ? totalHoursWorked / monthlyAttendance.length 
    : 0;

  const columns = [
    { 
      key: "clockInTime", 
      label: "Date", 
      sortable: true,
      render: (value: any) => value ? format(parseISO(value), 'MMM dd, yyyy') : "N/A"
    },
    { 
      key: "fullName", 
      label: "Name", 
      sortable: true,
      render: (value: any, row: any) => {
        const user = allStaff.find(s => s.id === row.userId);
        return user?.fullName || "N/A";
      }
    },
    { 
      key: "username", 
      label: "Username", 
      sortable: true,
      render: (value: any, row: any) => {
        const user = allStaff.find(s => s.id === row.userId);
        return user?.username || "Unknown";
      }
    },
    { 
      key: "role", 
      label: "Role", 
      sortable: true,
      render: (_: any, row: any) => {
        const user = allStaff.find(s => s.id === row.userId);
        return user?.role?.name?.replace(/_/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase()) || "N/A";
      }
    },
    { 
      key: "clockInTime", 
      label: "Clock In", 
      sortable: true,
      render: (value: any) => value ? formatDateTime(value) : "N/A"
    },
    { 
      key: "clockOutTime", 
      label: "Clock Out", 
      sortable: true,
      render: (value: any) => value ? formatDateTime(value) : "Still on duty"
    },
    { 
      key: "totalHours", 
      label: "Total Hours", 
      sortable: true,
      render: (value: any) => {
        const hours = Number(value) || 0;
        return hours > 0 ? `${hours.toFixed(2)} hrs` : "N/A";
      }
    },
    { 
      key: "status", 
      label: "Status", 
      render: (value: any) => (
        <Badge 
          variant={value === 'active' ? 'default' : 'secondary'}
          className={cn(
            value === 'active' 
              ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100' 
              : 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100'
          )}
        >
          {value === 'active' ? 'Active' : 'Completed'}
        </Badge>
      )
    }
  ];

  const currentAttendanceColumns = [
    { 
      key: "fullName", 
      label: "Name", 
      sortable: true 
    },
    { 
      key: "username", 
      label: "Username", 
      sortable: true 
    },
    { 
      key: "role", 
      label: "Role", 
      sortable: true,
      render: (value: any) => value?.name?.replace(/_/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase())
    },
    { 
      key: "id", 
      label: "Duty Status", 
      render: (_: any, row: any) => {
        const isOnDuty = activeAttendanceUserIds.includes(row.id);
        const attendance = dailyAttendance.find(a => a.userId === row.id && a.status === 'active');
        return (
          <div className="space-y-1">
            <Badge 
              variant={isOnDuty ? 'default' : 'secondary'}
              className={cn(
                isOnDuty 
                  ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100' 
                  : 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100'
              )}
            >
              {isOnDuty ? 'On Duty' : 'Off Duty'}
            </Badge>
            {attendance && (
              <div className="text-xs text-muted-foreground">
                Since {formatDateTime(attendance.clockInTime)}
              </div>
            )}
          </div>
        );
      }
    }
  ];

  return (
    <DashboardLayout title="Attendance Reports">
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatsCard
            title="Total Staff"
            value={allStaff.length}
            icon={<Users />}
            iconColor="text-blue-500"
            data-testid="stats-total-staff"
          />
          <StatsCard
            title="On Duty"
            value={onDutyStaff.length}
            icon={<UserCheck />}
            iconColor="text-green-500"
            data-testid="stats-on-duty"
          />
          <StatsCard
            title="Total Hours (This Month)"
            value={`${totalHoursWorked.toFixed(1)} hrs`}
            icon={<Clock />}
            iconColor="text-purple-500"
            data-testid="stats-total-hours"
          />
          <StatsCard
            title="Avg Hours/Day"
            value={`${avgHoursPerDay.toFixed(1)} hrs`}
            icon={<Calendar />}
            iconColor="text-orange-500"
            data-testid="stats-avg-hours"
          />
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <UserCheck className="w-5 h-5" />
              Current Duty Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <DataTable
              title="Staff Duty Status"
              data={allStaff}
              columns={currentAttendanceColumns}
              searchPlaceholder="Search staff..."
              isLoading={staffLoading}
              data-testid="table-current-duty"
            />
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="w-5 h-5" />
              Attendance Records - {format(selectedMonth, 'MMMM yyyy')}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <DataTable
              title="Attendance History"
              data={monthlyAttendance}
              columns={columns}
              searchPlaceholder="Search attendance records..."
              isLoading={allAttendanceLoading}
              data-testid="table-attendance-records"
            />
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/manager/leave.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { DataTable } from "@/components/tables/data-table";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Calendar, Clock, User, CheckCircle, XCircle, AlertCircle } from "lucide-react";
import { toast } from "sonner";
import { useRealtimeQuery } from "@/hooks/use-realtime-query";

export default function LeaveApprovals() {
  const [selectedRequest, setSelectedRequest] = useState<any>(null);
  const [isActionDialogOpen, setIsActionDialogOpen] = useState(false);
  const [actionType, setActionType] = useState<'approve' | 'reject'>('approve');
  const [managerNotes, setManagerNotes] = useState("");

  const queryClient = useQueryClient();

  // Fetch pending leave requests for approval
  const { data: pendingRequests = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/leave-requests/pending-approvals"],
    refetchInterval: 3000,
    queryFn: async () => {
      const response = await fetch("/api/hotels/current/leave-requests/pending-approvals", { 
        credentials: "include" 
      });
      if (!response.ok) throw new Error("Failed to fetch pending leave requests");
      return response.json();
    }
  });

  // Fetch all leave requests for overview
  const { data: allRequests = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/leave-requests"],
    refetchInterval: 3000,
    queryFn: async () => {
      const response = await fetch("/api/hotels/current/leave-requests", { 
        credentials: "include" 
      });
      if (!response.ok) throw new Error("Failed to fetch leave requests");
      return response.json();
    }
  });

  // Real-time updates for leave requests
  useRealtimeQuery({
    queryKey: ["/api/hotels/current/leave-requests/pending-approvals"],
    refetchInterval: 3000,
    events: ['leave:created', 'leave:updated']
  });

  useRealtimeQuery({
    queryKey: ["/api/hotels/current/leave-requests"],
    refetchInterval: 3000,
    events: ['leave:created', 'leave:updated']
  });

  // Approve leave request mutation
  const approveRequestMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes?: string }) => {
      const response = await fetch(`/api/leave-requests/${id}/approve`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ managerNotes: notes })
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to approve leave request");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/leave-requests/pending-approvals"] });
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/leave-requests"] });
      setIsActionDialogOpen(false);
      setSelectedRequest(null);
      setManagerNotes("");
      toast.success("Leave request approved successfully");
    },
    onError: (error: any) => {
      toast.error(error.message);
    }
  });

  // Reject leave request mutation
  const rejectRequestMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: string; notes: string }) => {
      const response = await fetch(`/api/leave-requests/${id}/reject`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ managerNotes: notes })
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to reject leave request");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/leave-requests/pending-approvals"] });
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/leave-requests"] });
      setIsActionDialogOpen(false);
      setSelectedRequest(null);
      setManagerNotes("");
      toast.success("Leave request rejected");
    },
    onError: (error: any) => {
      toast.error(error.message);
    }
  });

  const handleApprovalAction = (request: any, action: 'approve' | 'reject') => {
    setSelectedRequest(request);
    setActionType(action);
    setManagerNotes("");
    setIsActionDialogOpen(true);
  };

  const handleConfirmAction = () => {
    if (!selectedRequest) return;

    if (actionType === 'approve') {
      approveRequestMutation.mutate({
        id: selectedRequest.id,
        notes: managerNotes
      });
    } else {
      rejectRequestMutation.mutate({
        id: selectedRequest.id,
        notes: managerNotes
      });
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved': return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'rejected': return <XCircle className="h-4 w-4 text-red-500" />;
      default: return <Clock className="h-4 w-4 text-orange-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved': return 'bg-green-100 text-green-800 border-green-200';
      case 'rejected': return 'bg-red-100 text-red-800 border-red-200';
      default: return 'bg-orange-100 text-orange-800 border-orange-200';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getDaysDifference = (startDate: string, endDate: string) => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diffTime = Math.abs(end.getTime() - start.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    return diffDays;
  };

  const pendingColumns = [
    { 
      key: "requestedBy", 
      label: "Employee", 
      render: (value: string, row: any) => (
        <div className="flex items-center space-x-2">
          <User className="h-4 w-4 text-muted-foreground" />
          <span>{row.requestedByUser?.username || 'Unknown User'}</span>
        </div>
      )
    },
    { 
      key: "leaveType", 
      label: "Type", 
      sortable: true,
      render: (value: string) => (
        <span className="font-medium capitalize">
          {value?.replace(/_/g, ' ')}
        </span>
      )
    },
    { 
      key: "startDate", 
      label: "Start Date", 
      sortable: true,
      render: (value: string) => formatDate(value)
    },
    { 
      key: "endDate", 
      label: "End Date", 
      sortable: true,
      render: (value: string) => formatDate(value)
    },
    {
      key: "duration",
      label: "Duration",
      render: (value: any, row: any) => (
        <span className="text-sm text-muted-foreground">
          {getDaysDifference(row.startDate, row.endDate)} day(s)
        </span>
      )
    },
    { 
      key: "reason", 
      label: "Reason", 
      render: (value: string) => (
        <span className="text-sm text-muted-foreground max-w-xs truncate">
          {value || 'No reason provided'}
        </span>
      )
    },
    { 
      key: "createdAt", 
      label: "Submitted", 
      sortable: true,
      render: (value: string) => formatDate(value)
    }
  ];

  const allRequestsColumns = [
    ...pendingColumns,
    { 
      key: "status", 
      label: "Status", 
      render: (value: string) => (
        <div className="flex items-center space-x-2">
          {getStatusIcon(value)}
          <Badge variant="outline" className={getStatusColor(value)}>
            {value?.charAt(0).toUpperCase() + value?.slice(1)}
          </Badge>
        </div>
      )
    },
    {
      key: "managerNotes",
      label: "Notes",
      render: (value: string) => (
        <span className="text-sm text-muted-foreground max-w-xs truncate">
          {value || '-'}
        </span>
      )
    }
  ];

  const pendingActions = [
    { 
      label: "Approve", 
      action: (row: any) => handleApprovalAction(row, 'approve'),
      variant: "default" as const 
    },
    { 
      label: "Reject", 
      action: (row: any) => handleApprovalAction(row, 'reject'),
      variant: "destructive" as const 
    }
  ];

  const approvedRequests = allRequests.filter(r => r.status === 'approved').length;
  const rejectedRequests = allRequests.filter(r => r.status === 'rejected').length;

  return (
    <DashboardLayout title="Leave Request Approvals">
      <div className="space-y-6">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Pending Approval</p>
                  <p className="text-2xl font-bold">{pendingRequests.length}</p>
                </div>
                <Clock className="h-8 w-8 text-orange-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Approved</p>
                  <p className="text-2xl font-bold">{approvedRequests}</p>
                </div>
                <CheckCircle className="h-8 w-8 text-green-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Rejected</p>
                  <p className="text-2xl font-bold">{rejectedRequests}</p>
                </div>
                <XCircle className="h-8 w-8 text-red-500" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Pending Requests Table */}
        <DataTable
          title="Pending Leave Requests"
          data={pendingRequests}
          columns={pendingColumns}
          actions={pendingActions}
          isLoading={isLoading}
          searchPlaceholder="Search pending requests..."
        />

        {/* All Requests Table */}
        <DataTable
          title="All Leave Requests"
          data={allRequests}
          columns={allRequestsColumns}
          isLoading={isLoading}
          searchPlaceholder="Search all requests..."
        />

        {/* Approval/Rejection Dialog */}
        <Dialog open={isActionDialogOpen} onOpenChange={setIsActionDialogOpen}>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>
                {actionType === 'approve' ? 'Approve' : 'Reject'} Leave Request
              </DialogTitle>
            </DialogHeader>
            {selectedRequest && (
              <div className="space-y-4">
                <div className="bg-muted p-4 rounded-lg">
                  <h4 className="font-medium mb-2">Request Details</h4>
                  <div className="space-y-1 text-sm">
                    <p><strong>Employee:</strong> {selectedRequest.requestedByUser?.username || 'Unknown'}</p>
                    <p><strong>Type:</strong> {selectedRequest.leaveType?.replace(/_/g, ' ')}</p>
                    <p><strong>Period:</strong> {formatDate(selectedRequest.startDate)} - {formatDate(selectedRequest.endDate)}</p>
                    <p><strong>Duration:</strong> {getDaysDifference(selectedRequest.startDate, selectedRequest.endDate)} day(s)</p>
                    {selectedRequest.reason && (
                      <p><strong>Reason:</strong> {selectedRequest.reason}</p>
                    )}
                  </div>
                </div>

                <div>
                  <Label htmlFor="managerNotes">
                    Manager Notes {actionType === 'reject' ? '(Required)' : '(Optional)'}
                  </Label>
                  <Textarea
                    id="managerNotes"
                    value={managerNotes}
                    onChange={(e) => setManagerNotes(e.target.value)}
                    placeholder={`Add notes for the ${actionType} decision...`}
                    rows={3}
                  />
                </div>

                <div className="flex justify-end space-x-2">
                  <Button variant="outline" onClick={() => setIsActionDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button 
                    onClick={handleConfirmAction}
                    disabled={approveRequestMutation.isPending || rejectRequestMutation.isPending || (actionType === 'reject' && !managerNotes.trim())}
                    variant={actionType === 'approve' ? 'default' : 'destructive'}
                  >
                    {(approveRequestMutation.isPending || rejectRequestMutation.isPending) 
                      ? (actionType === 'approve' ? "Approving..." : "Rejecting...") 
                      : (actionType === 'approve' ? "Approve Request" : "Reject Request")
                    }
                  </Button>
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/finance/maint.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Wrench, Send, Clock, CheckCircle2 } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";

export default function FinanceMaintenanceRequests() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [priority, setPriority] = useState("medium");
  const [location, setLocation] = useState("");
  const [photo, setPhoto] = useState<string>("");

  const { data: myRequests = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/maintenance-requests"],
    refetchInterval: 3000,
    select: (data: any) => {
      return data.filter((req: any) => req.reportedBy?.id === user?.id || req.reportedBy === user?.id);
    }
  });

  const createRequestMutation = useMutation({
    mutationFn: async (requestData: any) => {
      await apiRequest("POST", "/api/hotels/current/maintenance-requests", requestData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/maintenance-requests"] });
      toast({ title: "Maintenance request submitted successfully to Manager" });
      setTitle("");
      setDescription("");
      setPriority("medium");
      setLocation("");
      setPhoto("");
    },
    onError: () => {
      toast({ 
        title: "Failed to submit request", 
        description: "Please try again later",
        variant: "destructive" 
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!title || !description || !location || !photo) {
      toast({ 
        title: "Missing information", 
        description: "Please fill in all required fields including photo",
        variant: "destructive" 
      });
      return;
    }

    createRequestMutation.mutate({
      title,
      description,
      priority,
      location,
      photo,
      status: 'pending'
    });
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800 border-red-200 dark:bg-red-950 dark:text-red-300 dark:border-red-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200 dark:bg-yellow-950 dark:text-yellow-300 dark:border-yellow-800';
      case 'low':
        return 'bg-green-100 text-green-800 border-green-200 dark:bg-green-950 dark:text-green-300 dark:border-green-800';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200 dark:bg-gray-800 dark:text-gray-300 dark:border-gray-700';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle2 className="h-5 w-5 text-green-600 dark:text-green-400" />;
      case 'in_progress':
        return <Wrench className="h-5 w-5 text-blue-600 dark:text-blue-400" />;
      case 'pending':
      default:
        return <Clock className="h-5 w-5 text-orange-500 dark:text-orange-400" />;
    }
  };

  return (
    <DashboardLayout title="Maintenance Requests">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Send className="h-5 w-5 mr-2" />
              Submit Request to Manager
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="title">Issue Title *</Label>
                <Input
                  id="title"
                  placeholder="e.g., Office AC Not Working"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                  data-testid="input-maintenance-title"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="location">Location *</Label>
                <Input
                  id="location"
                  placeholder="e.g., Finance Office, Accounting Department"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  required
                  data-testid="input-maintenance-location"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="priority">Priority *</Label>
                <Select value={priority} onValueChange={setPriority}>
                  <SelectTrigger id="priority" className="h-11" data-testid="select-maintenance-priority">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="low">Low - Can wait</SelectItem>
                    <SelectItem value="medium">Medium - Soon</SelectItem>
                    <SelectItem value="high">High - Urgent</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="description">Description *</Label>
                <Textarea
                  id="description"
                  placeholder="Provide detailed information about the issue..."
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows={4}
                  required
                  data-testid="input-maintenance-description"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="photo">Photo *</Label>
                <Input
                  id="photo"
                  type="file"
                  accept="image/*"
                  required
                  data-testid="input-maintenance-photo"
                  onChange={(e) => {
                    const file = e.target.files?.[0];
                    if (file) {
                      const reader = new FileReader();
                      reader.onloadend = () => {
                        setPhoto(reader.result as string);
                      };
                      reader.readAsDataURL(file);
                    }
                  }}
                />
                {photo && (
                  <div className="mt-2">
                    <img src={photo} alt="Preview" className="max-w-full h-32 object-cover rounded border" />
                  </div>
                )}
              </div>

              <Button 
                type="submit" 
                className="w-full h-11 min-h-11"
                disabled={createRequestMutation.isPending}
                data-testid="button-submit-maintenance"
              >
                {createRequestMutation.isPending ? (
                  "Submitting..."
                ) : (
                  <>
                    <Send className="h-4 w-4 mr-2" />
                    Submit to Manager
                  </>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>My Requests</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {myRequests.length === 0 ? (
                <p className="text-center text-muted-foreground py-8">
                  No maintenance requests submitted yet
                </p>
              ) : (
                myRequests.map((request: any) => (
                  <div
                    key={request.id}
                    className="p-4 border rounded-lg space-y-2 hover:bg-accent/50 transition-colors"
                    data-testid={`maintenance-request-${request.id}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3 flex-1">
                        {getStatusIcon(request.status)}
                        <div className="flex-1">
                          <h4 className="font-medium text-foreground leading-tight">
                            {request.title}
                          </h4>
                          <p className="text-sm text-muted-foreground mt-1">
                            {request.location}
                          </p>
                        </div>
                      </div>
                    </div>

                    <p className="text-sm text-muted-foreground pl-8">
                      {request.description}
                    </p>

                    <div className="flex flex-wrap gap-2 pl-8">
                      <Badge className={getPriorityColor(request.priority)} variant="outline">
                        {request.priority.toUpperCase()}
                      </Badge>
                      <Badge variant="secondary">
                        {request.status.replace('_', ' ').toUpperCase()}
                      </Badge>
                      <span className="text-xs text-muted-foreground">
                        {new Date(request.createdAt).toLocaleDateString()}
                      </span>
                    </div>

                    {request.resolvedAt && (
                      <p className="text-xs text-green-600 dark:text-green-400 pl-8">
                        ‚úì Resolved on {new Date(request.resolvedAt).toLocaleDateString()}
                      </p>
                    )}
                  </div>
                ))
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

