import { useEffect, useMemo } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { StatsCard } from "@/components/dashboard/stats-card";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { DollarSign, Bed, Users, TrendingUp, Package, AlertTriangle, Building2, Settings, Receipt, Shield } from "lucide-react";
import { formatCurrency } from "@/lib/utils";
import { useWebSocket } from "@/hooks/use-websocket";
import DateConverter from "@remotemerge/nepali-date-converter";

export default function OwnerDashboard() {
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();
  const ws = useWebSocket();

  const { data: transactions = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/transactions"],
    refetchInterval: 3000
  });

  const { data: rooms = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/rooms"],
    refetchInterval: 3000
  });

  const { data: users = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/users"],
    refetchInterval: 3000
  });

  const { data: inventory = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/inventory-items"],
    refetchInterval: 3000
  });

  const { data: hotel } = useQuery<any>({
    queryKey: ["/api/hotels/current"],
    refetchInterval: 3000
  });

  const { data: dailyAttendance = [] } = useQuery<any[]>({
    queryKey: ["/api/attendance/daily"],
    refetchInterval: 3000
  });

  // Real-time updates via WebSocket
  useEffect(() => {
    const unsubscribers = [
      ws.on('transaction:created', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/transactions"] });
      }),
      ws.on('transaction:updated', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/transactions"] });
      }),
      ws.on('room:updated', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/rooms"] });
      }),
      ws.on('stock:updated', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/inventory-items"] });
      }),
      ws.on('attendance:updated', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/attendance/daily"] });
      }),
      ws.on('guest:created', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current"] });
      }),
      ws.on('guest:updated', () => {
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current"] });
      })
    ];

    return () => {
      unsubscribers.forEach(unsub => unsub());
    };
  }, [ws, queryClient]);

  // Get current Nepali month and year
  const currentNepaliDate = useMemo(() => {
    const today = new Date();
    const converter = new DateConverter(`${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, '0')}-${String(today.getDate()).padStart(2, '0')}`);
    return converter.toBs();
  }, []);

  // Filter transactions for current Nepali month only (monthly reset)
  const currentMonthTransactions = useMemo(() => {
    return transactions.filter(t => {
      try {
        const txnDate = new Date(t.createdAt);
        const txnConverter = new DateConverter(`${txnDate.getFullYear()}-${String(txnDate.getMonth() + 1).padStart(2, '0')}-${String(txnDate.getDate()).padStart(2, '0')}`);
        const txnNepaliDate = txnConverter.toBs();

        // Check if transaction is in current Nepali month and year
        return txnNepaliDate.year === currentNepaliDate.year && 
               txnNepaliDate.month === currentNepaliDate.month;
      } catch (error) {

        return false;
      }
    });
  }, [transactions, currentNepaliDate]);

  // Calculate revenue for current Nepali month only
  // Include all revenue-generating transaction types: revenue, cash_in, pos_in, fonepay_in
  const totalRevenue = currentMonthTransactions
    .filter(t => ['revenue', 'cash_in', 'pos_in', 'fonepay_in'].includes(t.txnType))
    .reduce((sum, t) => sum + (Number(t.amount) || 0), 0);

  const occupiedRooms = rooms.filter(r => r.isOccupied).length;
  const occupancyRate = rooms.length > 0 ? (occupiedRooms / rooms.length) * 100 : 0;
  const lowStockItems = inventory.filter(item => 
    Number(item.baseStockQty) <= Number(item.reorderLevel)
  ).length;


  return (
    <DashboardLayout title="Owner Dashboard" currentHotel={hotel?.name}>
      <div className="space-y-6">
        {/* Key Metrics Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatsCard
            title="Total Revenue"
            value={formatCurrency(totalRevenue)}
            icon={<DollarSign />}
            iconColor="text-green-500"
          />
          <StatsCard
            title="Room Occupancy"
            value={`${occupancyRate.toFixed(0)}%`}
            icon={<Bed />}
            iconColor="text-blue-500"
          />
          <StatsCard
            title="Total Staff"
            value={users.length}
            icon={<Users />}
            iconColor="text-purple-500"
          />
          <StatsCard
            title="Low Stock Items"
            value={lowStockItems}
            icon={<AlertTriangle />}
            iconColor="text-orange-500"
          />
        </div>

        {/* Quick Actions */}
        <Card>
          <CardHeader>
            <CardTitle>Owner Operations</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <Button variant="outline" className="h-20 flex flex-col items-center justify-center gap-0.5 p-0 [&_svg]:size-6" data-testid="button-audit" onClick={() => setLocation("/owner/audit-transparency")}>
                <Shield className="h-6 w-6" />
                <span className="text-sm">Audit & Transparency</span>
              </Button>
              <Button variant="outline" className="h-20 flex flex-col items-center justify-center gap-0.5 p-0 [&_svg]:size-6" data-testid="button-financial" onClick={() => setLocation("/owner/financial")}>
                <Receipt className="h-6 w-6" />
                <span className="text-sm">Financial</span>
              </Button>
              <Button variant="outline" className="h-20 flex flex-col items-center justify-center gap-0.5 p-0 [&_svg]:size-6" data-testid="button-occupancy" onClick={() => setLocation("/owner/occupancy")}>
                <Bed className="h-6 w-6" />
                <span className="text-sm">Occupancy</span>
              </Button>
              <Button variant="outline" className="h-20 flex flex-col items-center justify-center gap-0.5 p-0 [&_svg]:size-6" data-testid="button-staff" onClick={() => setLocation("/owner/staff")}>
                <Users className="h-6 w-6" />
                <span className="text-sm">Staff</span>
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Quick Overview Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Financial Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Financial Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span>Total Revenue:</span>
                  <span className="font-bold text-green-600">{formatCurrency(totalRevenue)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Transactions:</span>
                  <span className="font-medium">{transactions.length}</span>
                </div>
                <div className="text-sm text-muted-foreground">
                  View detailed financial reports in Financial Overview
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Room Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Room Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span>Total Rooms:</span>
                  <span className="font-bold">{rooms.length}</span>
                </div>
                <div className="flex justify-between">
                  <span>Occupied:</span>
                  <span className="font-medium text-blue-600">{occupiedRooms}</span>
                </div>
                <div className="flex justify-between">
                  <span>Available:</span>
                  <span className="font-medium text-green-600">{rooms.length - occupiedRooms}</span>
                </div>
                <div className="text-sm text-muted-foreground">
                  View detailed room status in Room Occupancy
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Staff Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Staff Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span>Total Staff:</span>
                  <span className="font-bold">{users.length}</span>
                </div>
                <div className="flex justify-between">
                  <span>On Duty:</span>
                  <span className="font-medium text-green-600">{users.filter(u => dailyAttendance.some(a => a.userId === u.id && a.status === 'active')).length}</span>
                </div>
                <div className="flex justify-between">
                  <span>Active:</span>
                  <span className="font-medium">{users.filter(u => u.isActive).length}</span>
                </div>
                <div className="text-sm text-muted-foreground">
                  Manage staff in Staff Management
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Recent Activity */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle>Recent Transactions</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                {transactions.slice(0, 5).map((transaction, index) => (
                  <div key={transaction.id || index} className="flex justify-between items-center p-2 border-b">
                    <div>
                      <div className="text-sm font-medium">{transaction.purpose || transaction.txnType?.replace(/_/g, ' ')}</div>
                      <div className="text-xs text-muted-foreground">
                        {new Date(transaction.createdAt).toLocaleDateString()} ‚Ä¢ {transaction.paymentMethod?.replace(/_/g, ' ')}
                      </div>
                    </div>
                    <div className={`text-sm font-bold ${
                      ['revenue', 'cash_in', 'pos_in', 'fonepay_in'].includes(transaction.txnType) ? 'text-green-600' : 'text-red-600'
                    }`}>
                      {formatCurrency(Number(transaction.amount))}
                    </div>
                  </div>
                ))}
                {transactions.length === 0 && (
                  <div className="text-center py-4 text-muted-foreground">
                    No recent transactions
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Inventory Alerts</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                {inventory
                  .filter(item => Number(item.baseStockQty) <= Number(item.reorderLevel))
                  .slice(0, 5)
                  .map((item, index) => (
                    <div key={item.id || index} className="flex justify-between items-center p-2 border-b">
                      <div>
                        <div className="text-sm font-medium">{item.name}</div>
                        <div className="text-xs text-orange-600">Low Stock</div>
                      </div>
                      <div className="text-sm">
                        {Number(item.baseStockQty).toFixed(2)} {item.baseUnit}
                        {item.packageUnit && ` (${Number(item.packageStockQty).toFixed(2)} ${item.packageUnit})`}
                      </div>
                    </div>
                  ))}
                {lowStockItems === 0 && (
                  <div className="text-center py-4 text-muted-foreground">
                    All items are well stocked
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/owner/attenda.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { DataTable } from "@/components/tables/data-table";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { StatsCard } from "@/components/dashboard/stats-card";
import { Badge } from "@/components/ui/badge";
import { Calendar, Clock, Users, UserCheck } from "lucide-react";
import { formatDateTime, cn } from "@/lib/utils";
import { format, startOfMonth, endOfMonth, parseISO } from "date-fns";

export default function AttendanceReports() {
  const [selectedMonth, setSelectedMonth] = useState(new Date());

  const { data: allStaff = [], isLoading: staffLoading } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/users"],
    refetchInterval: 3000
  });

  const { data: dailyAttendance = [], isLoading: attendanceLoading } = useQuery<any[]>({
    queryKey: ["/api/attendance/daily"],
    refetchInterval: 3000
  });

  const { data: allAttendance = [], isLoading: allAttendanceLoading } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/attendance"],
    queryFn: async () => {
      const response = await fetch("/api/hotels/current/attendance", { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch attendance");
      return response.json();
    },
    refetchInterval: 3000
  });

  const activeAttendanceUserIds = dailyAttendance
    .filter(a => a.status === 'active')
    .map(a => a.userId);

  const onDutyStaff = allStaff.filter(s => activeAttendanceUserIds.includes(s.id));
  const offDutyStaff = allStaff.filter(s => !activeAttendanceUserIds.includes(s.id));

  const monthStart = startOfMonth(selectedMonth);
  const monthEnd = endOfMonth(selectedMonth);

  const monthlyAttendance = allAttendance.filter(record => {
    if (!record.clockInTime) return false;
    const clockInDate = parseISO(record.clockInTime);
    return clockInDate >= monthStart && clockInDate <= monthEnd;
  });

  const totalHoursWorked = monthlyAttendance.reduce((sum, record) => {
    return sum + (Number(record.totalHours) || 0);
  }, 0);

  const avgHoursPerDay = monthlyAttendance.length > 0 
    ? totalHoursWorked / monthlyAttendance.length 
    : 0;

  const columns = [
    { 
      key: "clockInTime", 
      label: "Date", 
      sortable: true,
      render: (value: any) => value ? format(parseISO(value), 'MMM dd, yyyy') : "N/A"
    },
    { 
      key: "fullName", 
      label: "Name", 
      sortable: true,
      render: (value: any, row: any) => {
        const user = allStaff.find(s => s.id === row.userId);
        return user?.fullName || "N/A";
      }
    },
    { 
      key: "username", 
      label: "Username", 
      sortable: true,
      render: (value: any, row: any) => {
        const user = allStaff.find(s => s.id === row.userId);
        return user?.username || "Unknown";
      }
    },
    { 
      key: "role", 
      label: "Role", 
      sortable: true,
      render: (_: any, row: any) => {
        const user = allStaff.find(s => s.id === row.userId);
        return user?.role?.name?.replace(/_/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase()) || "N/A";
      }
    },
    { 
      key: "clockInTime", 
      label: "Clock In", 
      sortable: true,
      render: (value: any) => value ? formatDateTime(value) : "N/A"
    },
    { 
      key: "clockOutTime", 
      label: "Clock Out", 
      sortable: true,
      render: (value: any) => value ? formatDateTime(value) : "Still on duty"
    },
    { 
      key: "totalHours", 
      label: "Total Hours", 
      sortable: true,
      render: (value: any) => {
        const hours = Number(value) || 0;
        return hours > 0 ? `${hours.toFixed(2)} hrs` : "N/A";
      }
    },
    { 
      key: "status", 
      label: "Status", 
      render: (value: any) => (
        <Badge 
          variant={value === 'active' ? 'default' : 'secondary'}
          className={cn(
            value === 'active' 
              ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100' 
              : 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100'
          )}
        >
          {value === 'active' ? 'Active' : 'Completed'}
        </Badge>
      )
    }
  ];

  const currentAttendanceColumns = [
    { 
      key: "fullName", 
      label: "Name", 
      sortable: true 
    },
    { 
      key: "username", 
      label: "Username", 
      sortable: true 
    },
    { 
      key: "role", 
      label: "Role", 
      sortable: true,
      render: (value: any) => value?.name?.replace(/_/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase())
    },
    { 
      key: "id", 
      label: "Duty Status", 
      render: (_: any, row: any) => {
        const isOnDuty = activeAttendanceUserIds.includes(row.id);
        const attendance = dailyAttendance.find(a => a.userId === row.id && a.status === 'active');
        return (
          <div className="space-y-1">
            <Badge 
              variant={isOnDuty ? 'default' : 'secondary'}
              className={cn(
                isOnDuty 
                  ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100' 
                  : 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100'
              )}
            >
              {isOnDuty ? 'On Duty' : 'Off Duty'}
            </Badge>
            {attendance && (
              <div className="text-xs text-muted-foreground">
                Since {formatDateTime(attendance.clockInTime)}
              </div>
            )}
          </div>
        );
      }
    }
  ];

  return (
    <DashboardLayout title="Attendance Reports">
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatsCard
            title="Total Staff"
            value={allStaff.length}
            icon={<Users />}
            iconColor="text-blue-500"
            data-testid="stats-total-staff"
          />
          <StatsCard
            title="On Duty"
            value={onDutyStaff.length}
            icon={<UserCheck />}
            iconColor="text-green-500"
            data-testid="stats-on-duty"
          />
          <StatsCard
            title="Total Hours (This Month)"
            value={`${totalHoursWorked.toFixed(1)} hrs`}
            icon={<Clock />}
            iconColor="text-purple-500"
            data-testid="stats-total-hours"
          />
          <StatsCard
            title="Avg Hours/Day"
            value={`${avgHoursPerDay.toFixed(1)} hrs`}
            icon={<Calendar />}
            iconColor="text-orange-500"
            data-testid="stats-avg-hours"
          />
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <UserCheck className="w-5 h-5" />
              Current Duty Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <DataTable
              title="Staff Duty Status"
              data={allStaff}
              columns={currentAttendanceColumns}
              searchPlaceholder="Search staff..."
              isLoading={staffLoading}
              data-testid="table-current-duty"
            />
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="w-5 h-5" />
              Attendance Records - {format(selectedMonth, 'MMMM yyyy')}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <DataTable
              title="Attendance History"
              data={monthlyAttendance}
              columns={columns}
              searchPlaceholder="Search attendance records..."
              isLoading={allAttendanceLoading}
              data-testid="table-attendance-records"
            />
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

