import { z } from "zod";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Badge } from "@/components/ui/badge";
import { Card } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Calendar as CalendarIcon, Clock, Users, DollarSign, Package, CheckCircle, Loader2, Search, AlertCircle, Plus, Trash2 } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { cn, formatCurrency } from "@/lib/utils";
import { format } from "date-fns";
import type { Hall, SelectServicePackage, Guest } from "@shared/schema";

interface ServiceItem {
  id: string;
  name: string;
  quantity: number;
  unitPrice: number;
}

const wizardSchema = z.object({
  // Step 1: Hall & Time
  hallId: z.string().min(1, "Hall is required"),
  date: z.date(),
  startTime: z.string().min(1, "Start time is required"),
  endTime: z.string().min(1, "End time is required"),
  duration: z.number().min(1, "Duration must be at least 1 hour"),
  isInHouseGuest: z.boolean().default(false),

  // Step 2: Customer Info
  customerName: z.string().min(1, "Customer name is required"),
  customerPhone: z.string().min(1, "Phone is required"),
  customerEmail: z.string().email().optional().or(z.literal("")),
  guestId: z.string().optional(),
  numberOfPeople: z.number().min(1, "Estimated number of people is required"),

  // Step 3: Payment (quotation only)
  totalAmount: z.number(),
  advancePaid: z.number().min(0, "Advance cannot be negative"),
  balanceDue: z.number(),
  paymentMethod: z.string().optional(),
  specialRequests: z.string().optional()
}).refine((data) => data.advancePaid <= data.totalAmount, {
  message: "Advance payment cannot exceed total amount",
  path: ["advancePaid"]
});

type WizardFormData = z.infer<typeof wizardSchema>;

interface BookingWizardProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSuccess: () => void;
}

export function BookingWizard({ open, onOpenChange, onSuccess }: BookingWizardProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState(1);
  const [guestSearch, setGuestSearch] = useState("");
  const [isSearching, setIsSearching] = useState(false);
  const [availabilityStatus, setAvailabilityStatus] = useState<{ available: boolean; suggestions?: any[] } | null>(null);
  const [foodDescription, setFoodDescription] = useState("");
  const [perPersonFoodPrice, setPerPersonFoodPrice] = useState(0);
  const [serviceItems, setServiceItems] = useState<ServiceItem[]>([]);

  const form = useForm<WizardFormData>({
    resolver: zodResolver(wizardSchema),
    defaultValues: {
      hallId: "",
      date: new Date(),
      startTime: "",
      endTime: "",
      duration: 1,
      isInHouseGuest: false,
      customerName: "",
      customerPhone: "",
      customerEmail: "",
      numberOfPeople: 1,
      totalAmount: 0,
      advancePaid: 0,
      balanceDue: 0,
      paymentMethod: "",
      specialRequests: ""
    }
  });

  const { data: halls = [] } = useQuery<Hall[]>({
    queryKey: ["/api/halls"],
    enabled: !!user?.hotelId && open
  });


  const { data: hotelSettings } = useQuery({
    queryKey: ["/api/hotels/current"],
    enabled: !!user?.hotelId && open
  });

  const { data: hotelTaxes = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/taxes"],
    enabled: !!user?.hotelId && open
  });

  const watchedHallId = form.watch("hallId");
  const watchedDate = form.watch("date");
  const watchedStartTime = form.watch("startTime");
  const watchedEndTime = form.watch("endTime");
  const watchedDuration = form.watch("duration");
  const watchedNumberOfPeople = form.watch("numberOfPeople");

  const selectedHall = halls.find(h => h.id === watchedHallId);

  // Clear availability status when key fields change
  useEffect(() => {
    setAvailabilityStatus(null);
  }, [watchedHallId, watchedDate, watchedStartTime, watchedEndTime]);

  // Calculate duration when start/end time changes
  const calculateDuration = (start: string, end: string) => {
    if (!start || !end) return 0;
    const [startHour, startMin] = start.split(":").map(Number);
    const [endHour, endMin] = end.split(":").map(Number);
    const startMinutes = startHour * 60 + startMin;
    const endMinutes = endHour * 60 + endMin;
    return Math.max(0, (endMinutes - startMinutes) / 60);
  };

  // Update duration when times change
  const handleStartTimeChange = (value: string) => {
    form.setValue("startTime", value);
    const duration = calculateDuration(value, watchedEndTime);
    if (duration > 0) {
      form.setValue("duration", duration);
      calculateTotal();
    }
  };

  const handleEndTimeChange = (value: string) => {
    form.setValue("endTime", value);
    const duration = calculateDuration(watchedStartTime, value);
    if (duration > 0) {
      form.setValue("duration", duration);
      calculateTotal();
    }
  };

  // Check availability
  const checkAvailability = async (): Promise<boolean> => {
    if (!watchedHallId || !watchedDate || !watchedStartTime || !watchedEndTime) return true;

    // Clear any previous status first
    setAvailabilityStatus(null);

    try {
      const result = await apiRequest("POST", "/api/halls/check-availability-quick", {
        hallId: watchedHallId,
        date: format(watchedDate, "yyyy-MM-dd"),
        startTime: watchedStartTime,
        endTime: watchedEndTime
      }) as any;

      // Check if available is explicitly false
      if (result && result.available === false) {
        setAvailabilityStatus(result as { available: boolean; suggestions?: any[] });
        toast({
          title: "Hall Not Available",
          description: result.suggestions?.length 
            ? "Alternative time slots are suggested below" 
            : "No alternative slots available for this date",
          variant: "destructive"
        });
        return false;
      }

      // If available is true or not explicitly false, allow proceeding
      return true;
    } catch (error) {
      toast({
        title: "Error checking availability",
        variant: "destructive"
      });
      return false;
    }
  };

  // Service item management
  const addServiceItem = () => {
    setServiceItems([...serviceItems, {
      id: Date.now().toString(),
      name: "",
      quantity: 1,
      unitPrice: 0
    }]);
  };

  const updateServiceItem = (id: string, field: keyof ServiceItem, value: any) => {
    setServiceItems(serviceItems.map(item => 
      item.id === id ? { ...item, [field]: value } : item
    ));
  };

  const removeServiceItem = (id: string) => {
    setServiceItems(serviceItems.filter(item => item.id !== id));
  };

  const getServicesTotal = () => {
    return serviceItems.reduce((sum, item) => sum + (item.quantity * item.unitPrice), 0);
  };

  // Calculate quotation total (hall base price + food + services + taxes)
  const calculateTotal = () => {
    if (!selectedHall) return;

    const isInHouse = form.getValues("isInHouseGuest");
    const hourlyRate = isInHouse 
      ? (selectedHall.priceInhouse ? Number(selectedHall.priceInhouse) : 0)
      : (selectedHall.priceWalkin ? Number(selectedHall.priceWalkin) : 0);
    const basePrice = hourlyRate * watchedDuration;

    // Add food cost
    const foodCost = watchedNumberOfPeople * perPersonFoodPrice;

    // Add services cost
    const servicesTotal = getServicesTotal();

    const subtotal = basePrice + foodCost + servicesTotal;

    // Calculate taxes from database (VAT, service charge, luxury tax)
    let totalTax = 0;
    hotelTaxes.forEach((tax: any) => {
      if (tax.isActive) {
        const taxAmount = subtotal * (Number(tax.percent) / 100);
        totalTax += taxAmount;
      }
    });

    const total = subtotal + totalTax;

    form.setValue("totalAmount", Number(total.toFixed(2)));

    const advance = form.getValues("advancePaid") || 0;
    form.setValue("balanceDue", Number((total - advance).toFixed(2)));
  };

  // Search guest
  const searchGuest = async () => {
    if (!guestSearch) return;

    setIsSearching(true);
    try {
      const guest = await apiRequest("GET", `/api/guests/search?phone=${encodeURIComponent(guestSearch)}`) as any;

      if (guest) {
        form.setValue("customerName", `${guest.firstName} ${guest.lastName}`);
        form.setValue("customerPhone", guest.phone);
        form.setValue("customerEmail", guest.email || "");
        form.setValue("guestId", guest.id);
        toast({ title: "Guest found", description: "Information auto-filled" });
      } else {
        toast({ title: "Guest not found", description: "Please enter details manually" });
      }
    } catch (error) {
      toast({ title: "Search failed", variant: "destructive" });
    } finally {
      setIsSearching(false);
    }
  };

  // Recalculate when food or services change
  useEffect(() => {
    if (currentStep === 3) {
      calculateTotal();
    }
  }, [perPersonFoodPrice, serviceItems, currentStep]);

  const createBookingMutation = useMutation({
    mutationFn: async (data: WizardFormData) => {
      // Format food and services as description
      const servicesDescription = serviceItems
        .filter(item => item.name.trim())
        .map(item => `${item.name} (${item.quantity} √ó ${formatCurrency(item.unitPrice)} = ${formatCurrency(item.quantity * item.unitPrice)})`)
        .join(', ');

      const foodDescriptionText = perPersonFoodPrice > 0 
        ? `Food: ${foodDescription || 'Not specified'} (${data.numberOfPeople} persons √ó ${formatCurrency(perPersonFoodPrice)} = ${formatCurrency(data.numberOfPeople * perPersonFoodPrice)})`
        : '';

      const fullServicesDescription = [foodDescriptionText, servicesDescription]
        .filter(s => s)
        .join(' | ');

      const bookingData = {
        hallId: data.hallId,
        customerName: data.customerName,
        customerPhone: data.customerPhone,
        customerEmail: data.customerEmail || undefined,
        guestId: data.guestId || undefined,
        isInHouseGuest: data.isInHouseGuest,
        bookingStartTime: new Date(`${format(data.date, "yyyy-MM-dd")}T${data.startTime}`),
        bookingEndTime: new Date(`${format(data.date, "yyyy-MM-dd")}T${data.endTime}`),
        duration: data.duration.toString(),
        numberOfPeople: data.numberOfPeople,
        hallBasePrice: (data.isInHouseGuest 
          ? (selectedHall?.priceInhouse ? Number(selectedHall.priceInhouse) * data.duration : 0)
          : (selectedHall?.priceWalkin ? Number(selectedHall.priceWalkin) * data.duration : 0)
        ).toFixed(2),
        totalAmount: data.totalAmount.toString(),
        advancePaid: data.advancePaid.toString(),
        balanceDue: data.balanceDue.toString(),
        paymentMethod: data.paymentMethod || undefined,
        specialRequests: data.specialRequests || undefined,
        foodServices: fullServicesDescription || undefined,
        status: "quotation"
      };

      await apiRequest("POST", `/api/hotels/${user?.hotelId}/hall-bookings`, bookingData);
    },
    onSuccess: () => {
      toast({ title: "Booking created successfully" });
      form.reset();
      setCurrentStep(1);
      setAvailabilityStatus(null);
      setFoodDescription("");
      setPerPersonFoodPrice(0);
      setServiceItems([]);
      onSuccess();
      onOpenChange(false);
    },
    onError: (error: any) => {
      toast({
        title: "Booking failed",
        description: error.message || "Failed to create booking",
        variant: "destructive"
      });
    }
  });

  const handleNext = async () => {
    let isValid = false;

    switch (currentStep) {
      case 1:
        isValid = await form.trigger(["hallId", "date", "startTime", "endTime", "duration"]);
        if (!isValid) {
          toast({
            title: "Validation Error",
            description: "Please fill in all required fields correctly",
            variant: "destructive"
          });
          return;
        }
        const isAvailable = await checkAvailability();
        if (isAvailable) {
          setCurrentStep(2);
        }
        break;
      case 2:
        isValid = await form.trigger(["customerName", "customerPhone", "customerEmail", "numberOfPeople"]);
        if (!isValid) {
          toast({
            title: "Validation Error",
            description: "Please fill in all customer information",
            variant: "destructive"
          });
          return;
        }
        calculateTotal();
        setCurrentStep(3);
        break;
      case 3:
        form.handleSubmit((data) => createBookingMutation.mutate(data))();
        break;
    }
  };

  const handleBack = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const steps = [
    { number: 1, title: "Hall & Time", icon: CalendarIcon },
    { number: 2, title: "Customer Info", icon: Users },
    { number: 3, title: "Quotation", icon: DollarSign }
  ];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Create New Booking</DialogTitle>
        </DialogHeader>

        <div className="mb-6">
          <div className="flex justify-between items-center">
            {steps.map((step, index) => {
              const Icon = step.icon;
              const isActive = currentStep === step.number;
              const isCompleted = currentStep > step.number;

              return (
                <div key={step.number} className="flex items-center flex-1">
                  <div className="flex flex-col items-center">
                    <div className={cn(
                      "w-10 h-10 rounded-full flex items-center justify-center transition-colors",
                      isActive && "bg-primary text-primary-foreground",
                      isCompleted && "bg-green-500 text-white",
                      !isActive && !isCompleted && "bg-muted text-muted-foreground"
                    )}>
                      {isCompleted ? <CheckCircle className="w-5 h-5" /> : <Icon className="w-5 h-5" />}
                    </div>
                    <span className={cn(
                      "text-sm mt-2",
                      isActive && "font-semibold text-primary",
                      !isActive && "text-muted-foreground"
                    )}>
                      {step.title}
                    </span>
                  </div>
                  {index < steps.length - 1 && (
                    <Separator className={cn(
                      "flex-1 mx-2",
                      isCompleted && "bg-green-500"
                    )} />
                  )}
                </div>
              );
            })}
          </div>
        </div>

        <Form {...form}>
          <div className="space-y-4">
            {currentStep === 1 && (
              <>
                <FormField
                  control={form.control}
                  name="isInHouseGuest"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Customer Type</FormLabel>
                      <Select onValueChange={(value) => {
                        field.onChange(value === "true");
                        calculateTotal();
                      }} value={field.value ? "true" : "false"}>
                        <FormControl>
                          <SelectTrigger data-testid="select-customer-type">
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="false">Walk-in Customer</SelectItem>
                          <SelectItem value="true">In-House Guest</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="hallId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Select Hall</FormLabel>
                      <Select onValueChange={(value) => { field.onChange(value); calculateTotal(); }} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-hall-wizard">
                            <SelectValue placeholder="Choose a hall" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent className="max-h-[300px] overflow-y-auto">
                          {halls.map((hall) => (
                            <SelectItem key={hall.id} value={hall.id}>
                              {hall.name} - Capacity: {hall.capacity}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="date"
                  render={({ field }) => (
                    <FormItem className="flex flex-col">
                      <FormLabel>Date</FormLabel>
                      <Popover>
                        <PopoverTrigger asChild>
                          <FormControl>
                            <Button variant="outline" className={cn("w-full pl-3 text-left font-normal", !field.value && "text-muted-foreground")} data-testid="button-select-date">
                              {field.value ? format(field.value, "PPP") : <span>Pick a date</span>}
                              <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                            </Button>
                          </FormControl>
                        </PopoverTrigger>
                        <PopoverContent className="w-auto p-0" align="start">
                          <Calendar mode="single" selected={field.value} onSelect={field.onChange} disabled={(date) => date < new Date(new Date().setHours(0, 0, 0, 0))} initialFocus />
                        </PopoverContent>
                      </Popover>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="startTime"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Start Time</FormLabel>
                        <FormControl>
                          <Input type="time" {...field} onChange={(e) => { field.onChange(e); handleStartTimeChange(e.target.value); }} data-testid="input-start-time" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="endTime"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>End Time</FormLabel>
                        <FormControl>
                          <Input type="time" {...field} onChange={(e) => { field.onChange(e); handleEndTimeChange(e.target.value); }} data-testid="input-end-time" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="duration"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Duration: {field.value} hour(s)</FormLabel>
                      <FormControl>
                        <Slider
                          min={1}
                          max={24}
                          step={0.5}
                          value={[field.value]}
                          onValueChange={(value) => { field.onChange(value[0]); calculateTotal(); }}
                          data-testid="slider-duration"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {selectedHall && (
                  <Card className="p-4 bg-muted">
                    <div className="space-y-2">
                      <div className="flex justify-between">
                        <span className="text-sm text-muted-foreground">Price Type:</span>
                        <span className="font-medium">{form.watch("isInHouseGuest") ? "In-House Guest" : "Walk-in Customer"}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-muted-foreground">Hourly Rate:</span>
                        <span className="font-medium">
                          {formatCurrency(form.watch("isInHouseGuest") 
                            ? Number(selectedHall.priceInhouse || 0)
                            : Number(selectedHall.priceWalkin || 0)
                          )}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-muted-foreground">Base Price ({watchedDuration}h):</span>
                        <span className="font-medium">
                          {formatCurrency((form.watch("isInHouseGuest") 
                            ? Number(selectedHall.priceInhouse || 0)
                            : Number(selectedHall.priceWalkin || 0)
                          ) * watchedDuration)}
                        </span>
                      </div>
                    </div>
                  </Card>
                )}

                {availabilityStatus && !availabilityStatus.available && availabilityStatus.suggestions && availabilityStatus.suggestions.length > 0 && (
                  <Card className="p-4 border-yellow-500">
                    <div className="flex items-start gap-2">
                      <AlertCircle className="w-5 h-5 text-yellow-500 mt-0.5" />
                      <div className="space-y-2 flex-1">
                        <p className="text-sm font-medium">Alternative Time Slots:</p>
                        {availabilityStatus.suggestions.map((slot, idx) => (
                          <Button
                            key={idx}
                            variant="outline"
                            size="sm"
                            className="w-full justify-start"
                            onClick={() => {
                              const startTime = format(new Date(slot.startTime), "HH:mm");
                              const endTime = format(new Date(slot.endTime), "HH:mm");
                              form.setValue("startTime", startTime);
                              form.setValue("endTime", endTime);
                              handleStartTimeChange(startTime);
                              handleEndTimeChange(endTime);
                              setAvailabilityStatus(null);
                            }}
                          >
                            <Clock className="w-4 h-4 mr-2" />
                            {format(new Date(slot.startTime), "h:mm a")} - {format(new Date(slot.endTime), "h:mm a")}
                          </Button>
                        ))}
                      </div>
                    </div>
                  </Card>
                )}
              </>
            )}

            {currentStep === 2 && (
              <>
                <div className="flex gap-2">
                  <Input
                    placeholder="Search by phone or email"
                    value={guestSearch}
                    onChange={(e) => setGuestSearch(e.target.value)}
                    data-testid="input-guest-search"
                  />
                  <Button type="button" onClick={searchGuest} disabled={isSearching} data-testid="button-search-guest">
                    {isSearching ? <Loader2 className="w-4 h-4 animate-spin" /> : <Search className="w-4 h-4" />}
                  </Button>
                </div>

                <FormField
                  control={form.control}
                  name="customerName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Customer Name</FormLabel>
                      <FormControl>
                        <Input {...field} placeholder="Enter customer name" data-testid="input-customer-name" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="customerPhone"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Phone</FormLabel>
                        <FormControl>
                          <Input {...field} placeholder="Phone number" data-testid="input-customer-phone" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="customerEmail"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email (Optional)</FormLabel>
                        <FormControl>
                          <Input {...field} type="email" placeholder="email@example.com" data-testid="input-customer-email" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="numberOfPeople"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Number of People</FormLabel>
                      <FormControl>
                        <Input {...field} type="number" min={1} onChange={(e) => field.onChange(parseInt(e.target.value) || 1)} data-testid="input-number-people" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </>
            )}

            {currentStep === 3 && (
              <>
                <Separator className="my-4" />

                <div className="space-y-4">
                  <h3 className="font-semibold text-lg">Food Buffet (Per Person)</h3>

                  <div>
                    <label className="text-sm font-medium">Food Items Description</label>
                    <Textarea
                      value={foodDescription}
                      onChange={(e) => setFoodDescription(e.target.value)}
                      placeholder="E.g., Chicken curry, Dal, Vegetable curry, Rice, Naan, Mixed salad, Dessert..."
                      rows={3}
                      data-testid="textarea-food-description"
                    />
                    <p className="text-sm text-muted-foreground mt-1">Describe what food items are included in the buffet</p>
                  </div>

                  <div>
                    <label className="text-sm font-medium">Price Per Person</label>
                    <Input
                      type="number"
                      step="0.01"
                      min={0}
                      value={perPersonFoodPrice}
                      onChange={(e) => setPerPersonFoodPrice(parseFloat(e.target.value) || 0)}
                      data-testid="input-per-person-price"
                    />
                    <p className="text-sm text-muted-foreground mt-1">
                      Total food cost: {formatCurrency(watchedNumberOfPeople * perPersonFoodPrice)}
                    </p>
                  </div>
                </div>

                <Separator className="my-4" />

                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <h3 className="font-semibold text-lg">Additional Services</h3>
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={addServiceItem}
                      data-testid="button-add-service"
                    >
                      <Plus className="w-4 h-4 mr-1" />
                      Add Service
                    </Button>
                  </div>

                  {serviceItems.length === 0 ? (
                    <p className="text-sm text-muted-foreground">No additional services added. Click "Add Service" to include decoration, sound system, etc.</p>
                  ) : (
                    <div className="space-y-2">
                      {serviceItems.map((item) => (
                        <Card key={item.id} className="p-3">
                          <div className="grid grid-cols-12 gap-2 items-end">
                            <div className="col-span-5">
                              <label className="text-xs text-muted-foreground">Service Name</label>
                              <Input
                                placeholder="E.g., Decoration, Sound System"
                                value={item.name}
                                onChange={(e) => updateServiceItem(item.id, 'name', e.target.value)}
                                data-testid={`input-service-name-${item.id}`}
                              />
                            </div>
                            <div className="col-span-2">
                              <label className="text-xs text-muted-foreground">Qty</label>
                              <Input
                                type="number"
                                min={1}
                                value={item.quantity}
                                onChange={(e) => updateServiceItem(item.id, 'quantity', parseInt(e.target.value) || 1)}
                                data-testid={`input-service-qty-${item.id}`}
                              />
                            </div>
                            <div className="col-span-3">
                              <label className="text-xs text-muted-foreground">Unit Price</label>
                              <Input
                                type="number"
                                step="0.01"
                                min={0}
                                value={item.unitPrice}
                                onChange={(e) => updateServiceItem(item.id, 'unitPrice', parseFloat(e.target.value) || 0)}
                                data-testid={`input-service-price-${item.id}`}
                              />
                            </div>
                            <div className="col-span-2 flex items-center gap-2">
                              <div className="text-sm font-medium">
                                {formatCurrency(item.quantity * item.unitPrice)}
                              </div>
                              <Button
                                type="button"
                                variant="ghost"
                                size="sm"
                                onClick={() => removeServiceItem(item.id)}
                                data-testid={`button-remove-service-${item.id}`}
                              >
                                <Trash2 className="w-4 h-4 text-destructive" />
                              </Button>
                            </div>
                          </div>
                        </Card>
                      ))}
                    </div>
                  )}
                </div>

                <Separator className="my-4" />

                <Card className="p-4 bg-muted">
                  <h3 className="font-semibold mb-3">Quotation Breakdown</h3>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">Hall Base Price ({watchedDuration}h):</span>
                      <span className="font-medium">
                        {formatCurrency((form.watch("isInHouseGuest")
                          ? (selectedHall?.priceInhouse ? Number(selectedHall.priceInhouse) : 0)
                          : (selectedHall?.priceWalkin ? Number(selectedHall.priceWalkin) : 0)
                        ) * watchedDuration)}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">
                        Food ({watchedNumberOfPeople} √ó {formatCurrency(perPersonFoodPrice)}):
                      </span>
                      <span className="font-medium">{formatCurrency(watchedNumberOfPeople * perPersonFoodPrice)}</span>
                    </div>
                    {serviceItems.length > 0 && (
                      <div className="flex justify-between">
                        <span className="text-sm text-muted-foreground">Additional Services:</span>
                        <span className="font-medium">{formatCurrency(getServicesTotal())}</span>
                      </div>
                    )}
                    <Separator />
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">Subtotal:</span>
                      <span className="font-medium">
                        {formatCurrency((form.watch("isInHouseGuest")
                          ? (selectedHall?.priceInhouse ? Number(selectedHall.priceInhouse) : 0)
                          : (selectedHall?.priceWalkin ? Number(selectedHall.priceWalkin) : 0)
                        ) * watchedDuration + watchedNumberOfPeople * perPersonFoodPrice + getServicesTotal())}
                      </span>
                    </div>
                    {hotelTaxes.filter((tax: any) => tax.isActive).map((tax: any) => {
                      const subtotal = (form.watch("isInHouseGuest")
                        ? (selectedHall?.priceInhouse ? Number(selectedHall.priceInhouse) : 0)
                        : (selectedHall?.priceWalkin ? Number(selectedHall.priceWalkin) : 0)
                      ) * watchedDuration + watchedNumberOfPeople * perPersonFoodPrice + getServicesTotal();
                      const taxAmount = subtotal * (Number(tax.percent) / 100);

                      return (
                        <div key={tax.id} className="flex justify-between">
                          <span className="text-sm text-muted-foreground">
                            {tax.taxType.replace(/_/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase())} ({tax.percent}%):
                          </span>
                          <span className="font-medium">
                            {formatCurrency(taxAmount)}
                          </span>
                        </div>
                      );
                    })}
                    <Separator />
                    <div className="flex justify-between text-lg font-bold">
                      <span>Quotation Amount:</span>
                      <span>{formatCurrency(form.getValues("totalAmount"))}</span>
                    </div>
                  </div>
                </Card>

                <FormField
                  control={form.control}
                  name="advancePaid"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Advance Payment</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          step="0.01"
                          value={field.value}
                          onChange={(e) => {
                            const advance = Number(e.target.value || 0);
                            field.onChange(advance);
                            const total = form.getValues("totalAmount");
                            form.setValue("balanceDue", Number((total - advance).toFixed(2)));
                          }}
                          data-testid="input-advance-paid"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="paymentMethod"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Payment Method (Optional)</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-payment-method">
                            <SelectValue placeholder="Select payment method" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="cash">Cash</SelectItem>
                          <SelectItem value="pos">POS/Card</SelectItem>
                          <SelectItem value="fonepay">Fonepay</SelectItem>
                          <SelectItem value="bank_transfer">Bank Transfer</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="specialRequests"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Special Requests (Optional)</FormLabel>
                      <FormControl>
                        <Textarea {...field} placeholder="Any special requirements or notes" rows={3} data-testid="textarea-special-requests" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Card className="p-4 border-green-500 bg-green-50 dark:bg-green-950">
                  <div className="flex justify-between items-center">
                    <span className="font-medium">Balance Due:</span>
                    <span className="text-2xl font-bold text-green-600 dark:text-green-400">
                      {formatCurrency(form.getValues("balanceDue"))}
                    </span>
                  </div>
                </Card>
              </>
            )}
          </div>

          <div className="flex justify-between mt-6">
            <Button
              type="button"
              variant="outline"
              onClick={handleBack}
              disabled={currentStep === 1}
              data-testid="button-back"
            >
              Back
            </Button>
            <Button
              type="button"
              onClick={handleNext}
              disabled={createBookingMutation.isPending}
              data-testid="button-next"
            >
              {createBookingMutation.isPending && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
              {currentStep === 4 ? "Create Booking" : "Next"}
            </Button>
          </div>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/dashboard/cashier/maint.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Wrench, Send, Clock, CheckCircle2 } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { useRealtimeQuery } from "@/hooks/use-realtime-query";

export default function CashierMaintenance() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [priority, setPriority] = useState("medium");
  const [location, setLocation] = useState("");
  const [photo, setPhoto] = useState<string>("");

  const { data: myRequests = [] } = useQuery<any[]>({
    queryKey: ["/api/hotels/current/maintenance-requests"],
    refetchInterval: 3000,
    enabled: !!user,
    select: (data: any) => {
      return data.filter((req: any) => req.reportedBy?.id === user?.id);
    }
  });

  useRealtimeQuery({
    queryKey: ["/api/hotels/current/maintenance-requests"],
    refetchInterval: 3000,
    events: ['maintenance:created', 'maintenance:updated', 'maintenance:deleted']
  });

  const createRequestMutation = useMutation({
    mutationFn: async (requestData: any) => {
      await apiRequest("POST", "/api/hotels/current/maintenance-requests", requestData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/maintenance-requests"] });
      toast({ title: "Maintenance request submitted successfully" });
      setTitle("");
      setDescription("");
      setPriority("medium");
      setLocation("");
      setPhoto("");
    },
    onError: () => {
      toast({ 
        title: "Failed to submit request", 
        description: "Please try again later",
        variant: "destructive" 
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!title || !description || !location || !photo) {
      toast({ 
        title: "Missing information", 
        description: "Please fill in all required fields including photo",
        variant: "destructive" 
      });
      return;
    }

    createRequestMutation.mutate({
      title,
      description,
      priority,
      location,
      photo,
      status: 'pending'
    });
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low':
        return 'bg-green-100 text-green-800 border-green-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle2 className="h-5 w-5 text-green-600" />;
      case 'in_progress':
        return <Wrench className="h-5 w-5 text-blue-600" />;
      case 'pending':
      default:
        return <Clock className="h-5 w-5 text-orange-500" />;
    }
  };

  return (
    <DashboardLayout title="Maintenance Requests">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Send className="h-5 w-5 mr-2" />
              Submit New Request
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="title">Issue Title *</Label>
                <Input
                  id="title"
                  placeholder="e.g., POS system malfunction"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                  data-testid="input-title"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="location">Location *</Label>
                <Input
                  id="location"
                  placeholder="e.g., Cashier Counter, Front Desk"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  required
                  data-testid="input-location"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="priority">Priority *</Label>
                <Select value={priority} onValueChange={setPriority}>
                  <SelectTrigger id="priority" className="h-11" data-testid="select-priority">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="low">Low - Can wait</SelectItem>
                    <SelectItem value="medium">Medium - Soon</SelectItem>
                    <SelectItem value="high">High - Urgent</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="description">Description *</Label>
                <Textarea
                  id="description"
                  placeholder="Provide detailed information about the issue..."
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows={4}
                  required
                  data-testid="input-description"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="photo">Photo *</Label>
                <Input
                  id="photo"
                  type="file"
                  accept="image/*"
                  required
                  data-testid="input-photo"
                  onChange={(e) => {
                    const file = e.target.files?.[0];
                    if (file) {
                      const reader = new FileReader();
                      reader.onloadend = () => {
                        setPhoto(reader.result as string);
                      };
                      reader.readAsDataURL(file);
                    }
                  }}
                />
                {photo && (
                  <div className="mt-2">
                    <img src={photo} alt="Preview" className="max-w-full h-32 object-cover rounded border" data-testid="img-photo-preview" />
                  </div>
                )}
              </div>

              <Button 
                type="submit" 
                className="w-full h-11 min-h-11"
                disabled={createRequestMutation.isPending}
                data-testid="button-submit-request"
              >
                {createRequestMutation.isPending ? (
                  "Submitting..."
                ) : (
                  <>
                    <Send className="h-4 w-4 mr-2" />
                    Submit Request
                  </>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>My Requests</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {myRequests.length === 0 ? (
                <p className="text-center text-muted-foreground py-8" data-testid="text-no-requests">
                  No maintenance requests submitted yet
                </p>
              ) : (
                myRequests.map((request: any) => (
                  <div
                    key={request.id}
                    className="p-4 border rounded-lg space-y-2 hover:bg-accent/50 transition-colors"
                    data-testid={`card-request-${request.id}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3 flex-1">
                        {getStatusIcon(request.status)}
                        <div className="flex-1">
                          <h4 className="font-medium text-foreground leading-tight" data-testid={`text-title-${request.id}`}>
                            {request.title}
                          </h4>
                          <p className="text-sm text-muted-foreground mt-1" data-testid={`text-location-${request.id}`}>
                            {request.location}
                          </p>
                        </div>
                      </div>
                    </div>

                    <p className="text-sm text-muted-foreground pl-8" data-testid={`text-description-${request.id}`}>
                      {request.description}
                    </p>

                    <div className="flex flex-wrap gap-2 pl-8">
                      <Badge className={getPriorityColor(request.priority)} variant="outline" data-testid={`badge-priority-${request.id}`}>
                        {request.priority.toUpperCase()}
                      </Badge>
                      <Badge variant="secondary" data-testid={`badge-status-${request.id}`}>
                        {request.status.replace('_', ' ').toUpperCase()}
                      </Badge>
                      <span className="text-xs text-muted-foreground" data-testid={`text-date-${request.id}`}>
                        {new Date(request.createdAt).toLocaleDateString()}
                      </span>
                    </div>

                    {request.resolvedAt && (
                      <p className="text-xs text-green-600 pl-8" data-testid={`text-resolved-${request.id}`}>
                        ‚úì Resolved on {new Date(request.resolvedAt).toLocaleDateString()}
                      </p>
                    )}
                  </div>
                ))
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/dashboard/manager/vendo.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Ban, Plus, DollarSign, Calendar, User, FileText } from "lucide-react";
import { toast } from "sonner";
import { DashboardLayout } from "@/components/layout/dashboard-layout";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";

interface Vendor {
  id: string;
  name: string;
  contact: any;
  hotelId: string;
  createdAt: string;
}

interface Transaction {
  id: string;
  hotelId: string;
  txnType: string;
  amount: string;
  currency: string;
  paymentMethod: string;
  vendorId: string | null;
  purpose: string;
  reference: string;
  createdBy: string;
  createdAt: string;
  deletedAt: string | null;
  isVoided: boolean;
  voidedBy: string | null;
  voidedAt: string | null;
  voidReason: string | null;
}

export default function VendorPayments() {
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [showVoidDialog, setShowVoidDialog] = useState(false);
  const [selectedTransaction, setSelectedTransaction] = useState<Transaction | null>(null);
  const [voidReason, setVoidReason] = useState("");
  const [vendorName, setVendorName] = useState("");
  const [amount, setAmount] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("");
  const [purpose, setPurpose] = useState("");
  const [reference, setReference] = useState("");

  const queryClient = useQueryClient();

  // Get current user and hotel info
  const { data: currentUser, isLoading: userLoading } = useQuery<any>({
    queryKey: ["/api/user"],
    refetchInterval: 3000,
    queryFn: async () => {
      const response = await fetch("/api/user", { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch user");
      return response.json();
    }
  });

  // Fetch vendors for this hotel
  const { data: vendors = [], isLoading: vendorsLoading } = useQuery<Vendor[]>({
    queryKey: ["/api/hotels/current/vendors"],
    refetchInterval: 3000,
    enabled: !!currentUser?.hotelId,
    queryFn: async () => {
      const response = await fetch("/api/hotels/current/vendors", { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch vendors");
      return response.json();
    }
  });

  // Fetch vendor payments (transactions with vendorId)
  const { data: transactions = [], isLoading: transactionsLoading } = useQuery<Transaction[]>({
    queryKey: ["/api/hotels/current/transactions"],
    refetchInterval: 3000,
    enabled: !!currentUser?.hotelId,
    queryFn: async () => {
      const response = await fetch("/api/hotels/current/transactions", { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch transactions");
      const allTransactions = await response.json();
      // Filter to only vendor payments
      return allTransactions.filter((t: Transaction) => t.vendorId && t.txnType === "vendor_payment");
    }
  });

  // Create payment mutation
  const createPayment = useMutation({
    mutationFn: async (paymentData: any) => {
      if (!currentUser?.hotelId || !currentUser?.id) {
        throw new Error("User information not available");
      }

      const response = await fetch("/api/transactions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          ...paymentData,
          hotelId: currentUser.hotelId,
          txnType: "vendor_payment",
          createdBy: currentUser.id
        })
      });
      if (!response.ok) throw new Error("Failed to create payment");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/transactions"] });
      toast.success("Payment recorded successfully");
      resetForm();
    },
    onError: (error: Error) => {
      toast.error(error.message || "Failed to record payment");
    }
  });

  // Void payment mutation
  const voidPayment = useMutation({
    mutationFn: async ({ transactionId, reason }: { transactionId: string; reason: string }) => {
      const response = await fetch(`/api/transactions/${transactionId}/void`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ reason })
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to void payment");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/transactions"] });
      toast.success("Payment voided successfully");
      setShowVoidDialog(false);
      setVoidReason("");
      setSelectedTransaction(null);
    },
    onError: (error: Error) => {
      toast.error(error.message || "Failed to void payment");
    }
  });

  const resetForm = () => {
    setVendorName("");
    setAmount("");
    setPaymentMethod("");
    setPurpose("");
    setReference("");
    setShowPaymentForm(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!currentUser?.hotelId || !currentUser?.id) {
      toast.error("Please wait for user information to load");
      return;
    }

    if (!vendorName.trim() || !amount || !paymentMethod || !purpose || !reference.trim()) {
      toast.error("Please fill in all required fields");
      return;
    }

    if (isNaN(Number(amount)) || Number(amount) <= 0) {
      toast.error("Please enter a valid amount");
      return;
    }

    try {
      // Find or create vendor
      let vendor = vendors.find(v => v.name.toLowerCase() === vendorName.trim().toLowerCase());

      if (!vendor) {
        // Create new vendor
        const createResponse = await fetch("/api/hotels/current/vendors", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify({
            name: vendorName.trim(),
            contact: {}
          })
        });

        if (!createResponse.ok) {
          throw new Error("Failed to create vendor");
        }

        vendor = await createResponse.json();
        queryClient.invalidateQueries({ queryKey: ["/api/hotels/current/vendors"] });
      }

      if (!vendor) {
        throw new Error("Failed to get vendor information");
      }

      // Create payment with vendor ID
      createPayment.mutate({
        vendorId: vendor.id,
        amount: Number(amount).toFixed(2),
        currency: "NPR",
        paymentMethod,
        purpose,
        reference: reference.trim()
      });
    } catch (error: any) {
      toast.error(error.message || "Failed to process payment");
    }
  };

  const getVendorName = (vendorId: string) => {
    const vendor = vendors.find(v => v.id === vendorId);
    return vendor?.name || "Unknown Vendor";
  };

  const formatCurrency = (amount: string) => {
    return `NPR ${Number(amount).toLocaleString()}`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  if (userLoading || vendorsLoading || transactionsLoading) {
    return (
      <DashboardLayout title="Vendor Payments">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="h-32 bg-gray-200 rounded"></div>
          <div className="h-64 bg-gray-200 rounded"></div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout title="Vendor Payments">
      <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Vendor Payments</h1>
          <p className="text-gray-600 mt-1">Manage payments to vendors and suppliers</p>
        </div>
        <Button 
          onClick={() => setShowPaymentForm(true)}
          className="flex items-center gap-2"
        >
          <Plus className="h-4 w-4" />
          Record Payment
        </Button>
      </div>

      {/* Payment Stats */}
      <div className="grid md:grid-cols-3 gap-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Payments</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {formatCurrency(transactions.reduce((sum, t) => sum + Number(t.amount), 0).toFixed(2))}
            </div>
            <p className="text-xs text-muted-foreground">This month</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Vendors</CardTitle>
            <User className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{vendors.length}</div>
            <p className="text-xs text-muted-foreground">Registered vendors</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Recent Payments</CardTitle>
            <Calendar className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{transactions.length}</div>
            <p className="text-xs text-muted-foreground">Total records</p>
          </CardContent>
        </Card>
      </div>

      {/* Payment Form */}
      {showPaymentForm && (
        <Card>
          <CardHeader>
            <CardTitle>Record New Payment</CardTitle>
            <CardDescription>Enter payment details for vendor</CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="vendor">Vendor Name *</Label>
                  <Input
                    id="vendor"
                    placeholder="Enter vendor name"
                    value={vendorName}
                    onChange={(e) => setVendorName(e.target.value)}
                    required
                  />
                  <p className="text-xs text-muted-foreground">Type vendor name (will be created if doesn't exist)</p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="amount">Amount (NPR) *</Label>
                  <Input
                    id="amount"
                    type="number"
                    step="0.01"
                    placeholder="0.00"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="paymentMethod">Payment Method *</Label>
                  <Select value={paymentMethod} onValueChange={setPaymentMethod}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select method" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="cash">Cash</SelectItem>
                      <SelectItem value="cheque">Cheque</SelectItem>
                      <SelectItem value="bank_transfer">Bank Transfer</SelectItem>
                      <SelectItem value="digital_wallet">Digital Wallet</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="reference">Reference Number <span className="text-red-500">*</span></Label>
                  <Input
                    id="reference"
                    placeholder="Invoice/PO number, cheque number, etc."
                    value={reference}
                    onChange={(e) => setReference(e.target.value)}
                    data-testid="input-reference"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="purpose">Purpose *</Label>
                <Textarea
                  id="purpose"
                  placeholder="Description of payment purpose"
                  value={purpose}
                  onChange={(e) => setPurpose(e.target.value)}
                  required
                />
              </div>

              <div className="flex gap-3">
                <Button type="submit" disabled={createPayment.isPending}>
                  {createPayment.isPending ? "Recording..." : "Record Payment"}
                </Button>
                <Button type="button" variant="outline" onClick={resetForm}>
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}

      {/* Payment History */}
      <Card>
        <CardHeader>
          <CardTitle>Payment History</CardTitle>
          <CardDescription>Recent vendor payments</CardDescription>
        </CardHeader>
        <CardContent>
          {transactions.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <FileText className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No vendor payments recorded yet</p>
              <p className="text-sm">Click "Record Payment" to add your first payment</p>
            </div>
          ) : (
            <div className="space-y-4">
              {transactions.map((transaction) => (
                <div 
                  key={transaction.id}
                  className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50"
                >
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <h3 className="font-medium">{getVendorName(transaction.vendorId!)}</h3>
                      <Badge variant="outline" className="capitalize">
                        {transaction.paymentMethod.replace('_', ' ')}
                      </Badge>
                    </div>
                    <p className="text-sm text-gray-600 mb-1">{transaction.purpose}</p>
                    <p className="text-xs text-gray-500">{formatDate(transaction.createdAt)}</p>
                    {transaction.reference && (
                      <p className="text-xs text-gray-500">Ref: {transaction.reference}</p>
                    )}
                  </div>

                  <div className="text-right">
                    <p className="font-semibold text-lg">{formatCurrency(transaction.amount)}</p>
                    {transaction.isVoided ? (
                      <Badge variant="destructive" className="mt-2">VOIDED</Badge>
                    ) : (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => {
                          setSelectedTransaction(transaction);
                          setShowVoidDialog(true);
                        }}
                        className="text-orange-600 hover:text-orange-700 hover:bg-orange-50"
                        disabled={voidPayment.isPending}
                        data-testid={`button-void-${transaction.id}`}
                      >
                        <Ban className="h-4 w-4" />
                      </Button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>

    {/* Void Transaction Dialog */}
    <Dialog open={showVoidDialog} onOpenChange={setShowVoidDialog}>
      <DialogContent data-testid="dialog-void-transaction">
        <DialogHeader>
          <DialogTitle>Void Payment Transaction</DialogTitle>
          <DialogDescription>
            Voiding a payment transaction is permanent and creates an audit trail. 
            Please provide a detailed reason (minimum 15 characters).
          </DialogDescription>
        </DialogHeader>
        <div className="space-y-4 py-4">
          <div className="space-y-2">
            <Label htmlFor="void-reason">Reason for Voiding</Label>
            <Textarea
              id="void-reason"
              placeholder="e.g., Duplicate payment entry, incorrect amount, payment reversal requested..."
              value={voidReason}
              onChange={(e) => setVoidReason(e.target.value)}
              rows={4}
              data-testid="input-void-reason"
            />
            <p className="text-sm text-gray-500">
              {voidReason.length}/15 characters minimum
            </p>
          </div>
          {selectedTransaction && (
            <div className="p-3 bg-gray-50 rounded-lg space-y-1">
              <p className="text-sm font-medium">Transaction Details:</p>
              <p className="text-sm text-gray-600">Amount: {formatCurrency(selectedTransaction.amount)}</p>
              <p className="text-sm text-gray-600">Vendor: {getVendorName(selectedTransaction.vendorId!)}</p>
              <p className="text-sm text-gray-600">Purpose: {selectedTransaction.purpose}</p>
            </div>
          )}
        </div>
        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => {
              setShowVoidDialog(false);
              setVoidReason("");
              setSelectedTransaction(null);
            }}
            data-testid="button-cancel-void"
          >
            Cancel
          </Button>
          <Button
            variant="destructive"
            onClick={() => {
              if (selectedTransaction && voidReason.trim().length >= 15) {
                voidPayment.mutate({
                  transactionId: selectedTransaction.id,
                  reason: voidReason.trim()
                });
              }
            }}
            disabled={voidReason.trim().length < 15 || voidPayment.isPending}
            data-testid="button-confirm-void"
          >
            {voidPayment.isPending ? "Voiding..." : "Void Transaction"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
    </DashboardLayout>
  );
}

